import React from "react";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";

const offers = [
  {
    title: "Vacanță în Grecia",
    price: "De la 199€ pe persoană",
    image: "/images/grecia.jpg",
    bgColor: "from-pink-800 to-pink-600",
  },
  {
    title: "Vacanță în Italia",
    price: "De la 250€ pe persoană",
    image: "/images/italia.jpg",
    bgColor: "from-green-800 to-green-600",
  },
  {
    title: "Vacanță în Spania",
    price: "De la 199€ pe persoană",
    image: "/images/spania.jpg",
    bgColor: "from-blue-800 to-blue-600",
  },
  {
    title: "Vacanță în Franța",
    price: "De la 280€ pe persoană",
    image: "/images/franta.jpg",
    bgColor: "from-yellow-800 to-yellow-600",
  },
  {
    title: "Vacanță în Portugalia",
    price: "De la 230€ pe persoană",
    image: "/images/portugalia.jpg",
    bgColor: "from-red-800 to-red-600",
  },
];

const HomePage = () => {
  return (
    <div className="min-h-screen bg-gray-900">
      <Navbar />

      <main className="max-w-6xl mx-auto p-8">
        <h2 className="text-5xl font-extrabold mb-16 text-center text-white drop-shadow-lg">
          Oferte de sezon
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-16">
          {offers.map((offer, index) => (
            <div
              key={index}
              className={`relative rounded-3xl overflow-hidden shadow-2xl border border-white border-opacity-20 group cursor-pointer transform hover:scale-105 transition-transform duration-300 bg-gradient-to-br ${offer.bgColor}`}
            >
              {/* Overlay pentru contrast */}
              <div className="absolute inset-0 bg-black bg-opacity-60 z-0"></div>

              {/* Imagine */}
              <div className="relative z-10 overflow-hidden rounded-3xl h-80">
                <img
                  src={offer.image}
                  alt={offer.title}
                  className="w-full h-full object-cover object-center transition-transform duration-700 group-hover:scale-125"
                />
              </div>

              {/* Text și buton */}
              <div className="absolute bottom-0 left-0 right-0 p-8 bg-gradient-to-t from-black via-transparent z-20 rounded-b-3xl">
                <h3 className="text-4xl font-bold mb-3 text-white drop-shadow-lg">
                  {offer.title}
                </h3>
                <p className="text-2xl mb-6 text-white drop-shadow-md">
                  {offer.price}
                </p>
                <Link
                  to="/flights"
                  className="inline-block bg-white text-gray-900 font-semibold px-8 py-3 rounded-full shadow-lg hover:bg-gray-100 focus:outline-none focus:ring-4 focus:ring-gray-300 transition"
                >
                  Rezervă acum!
                </Link>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
