import React, { useState } from "react";

const CheckInPage = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [passportNumber, setPassportNumber] = useState("");
  const [luggageOption, setLuggageOption] = useState("");
  const [checkInStatus, setCheckInStatus] = useState<string | null>(null);

  const handleCheckIn = async (e: React.FormEvent) => {
    e.preventDefault();

    const clientId = localStorage.getItem("idClient"); // preluăm din localStorage

    if (!flightNumber || !passportNumber || !luggageOption) {
      setCheckInStatus("Te rugăm să completezi toate câmpurile.");
      return;
    }

    if (!clientId) {
      setCheckInStatus("Trebuie să fii logat pentru check-in.");
      return;
    }

    try {
      const response = await fetch("https://localhost:7239/api/CheckIn", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          IDZbor: parseInt(flightNumber),
          IDClient: parseInt(clientId),
          NrPasaport: parseInt(passportNumber),
          TipBagaj: luggageOption,
          CheckedIN: true,
        }),
      });

      if (!response.ok) {
        throw new Error("Eroare la check-in");
      }

      setCheckInStatus("Check-in reușit! Ai finalizat procedura.");
    } catch (error) {
      console.error(error);
      setCheckInStatus("A apărut o eroare la check-in.");
    }
  };

  return (
    <div className="w-full min-h-screen bg-gray-100 flex justify-center items-center py-10 px-4">
      <div className="bg-white p-8 rounded-xl shadow-lg max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center text-gray-700">
          Completează informațiile pentru Check-in
        </h2>

        <form onSubmit={handleCheckIn} className="space-y-6">
          <div>
            <label htmlFor="flightNumber" className="block text-lg font-semibold text-gray-600">
              Număr Zbor
            </label>
            <input
              type="number"
              id="flightNumber"
              value={flightNumber}
              onChange={(e) => setFlightNumber(e.target.value)}
              className="w-full border p-3 rounded mt-2"
              placeholder="Introdu numărul zborului"
              required
            />
          </div>

          <div>
            <label htmlFor="passportNumber" className="block text-lg font-semibold text-gray-600">
              Număr Pașaport
            </label>
            <input
              type="number"
              id="passportNumber"
              value={passportNumber}
              onChange={(e) => setPassportNumber(e.target.value)}
              className="w-full border p-3 rounded mt-2"
              placeholder="Introdu numărul pașaportului"
              required
            />
          </div>

          <div>
            <label htmlFor="luggage" className="block text-lg font-semibold text-gray-600">
              Tip Bagaj
            </label>
            <select
              id="luggage"
              value={luggageOption}
              onChange={(e) => setLuggageOption(e.target.value)}
              className="w-full border p-3 rounded mt-2"
              required
            >
              <option value="">Selectează</option>
              <option value="Bagaj de cabină">Bagaj de cabină (max. 8kg)</option>
              <option value="Bagaj de cală">Bagaj de cală (max. 23kg)</option>
              <option value="Bagaj suplimentar">Bagaj suplimentar</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded hover:bg-blue-700 transition"
          >
            Check-in
          </button>
        </form>

        {checkInStatus && (
          <div className="mt-6 text-center text-lg font-semibold text-gray-800">
            <p>{checkInStatus}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CheckInPage;
