import { useEffect, useState } from "react"; 
import axios from "axios";
import { useNavigate } from "react-router-dom";

interface Client {
  idClient: number;
  emailClient: string;
  numeClient: string;
  prenumeClient: string;
}

interface Rezervare {
  id: number;
  dataRezervare: string;
  ruta: {
    orasPlecare: string;
    orasSosire: string;
  };
}

const ProfilePage = () => {
  const [client, setClient] = useState<Client | null>(null);
  const [rezervari, setRezervari] = useState<Rezervare[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const clientId = localStorage.getItem("clientId");
    if (!clientId) {
      return;
    }

    axios.get(`https://localhost:7239/api/Client/${clientId}`)
      .then(response => {
        setClient(response.data);
      })
      .catch(error => {
        console.error("Eroare la preluarea datelor client:", error);
      });

    axios.get(`https://localhost:7239/api/Rezervari/client/${clientId}`)
      .then(response => {
        setRezervari(response.data);
      })
      .catch(error => {
        console.error("Eroare la preluarea rezervărilor:", error);
      });
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("clientId");
    navigate("/login");
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) {
      return "Data invalidă";
    }
    return date.toLocaleDateString() + " " + date.toLocaleTimeString();
  };

  return (
    <div className="max-w-3xl mx-auto p-8 bg-white rounded-2xl shadow-lg mt-10">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-900">Profilul Meu</h2>
      {client ? (
        <div>
          <div className="mb-8 space-y-2 text-gray-800">
            <p><span className="font-semibold">Nume:</span> {client.numeClient}</p>
            <p><span className="font-semibold">Prenume:</span> {client.prenumeClient}</p>
            <p><span className="font-semibold">Email:</span> {client.emailClient}</p>
          </div>

          <h3 className="text-2xl font-semibold mb-4 text-gray-900">Rezervările mele</h3>
          {rezervari.length > 0 ? (
            <div className="space-y-4">
              {rezervari.map((rezervare) => (
                <div
                  key={rezervare.id}
                  className="p-4 border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition"
                >
                  <p className="font-medium text-blue-600">
                    Ruta: {rezervare.ruta.orasPlecare} → {rezervare.ruta.orasSosire}
                  </p>
                  <p className="text-gray-600">
                    Data rezervării: {formatDate(rezervare.dataRezervare)}
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">Nu ai rezervări făcute.</p>
          )}

          <button
            onClick={handleLogout}
            className="mt-10 w-full py-3 bg-red-600 hover:bg-red-700 text-white rounded-xl font-semibold transition"
          >
            Logout
          </button>
        </div>
      ) : (
        <p className="text-center text-gray-600 mt-6">Nu ești autentificat.</p>
      )}
    </div>
  );
};

export default ProfilePage;
