import React, { useState } from "react";
import { InformationCircleIcon } from "@heroicons/react/24/outline";


const Tooltip = ({ text }: { text: string }) => (
  <span className="relative group inline-block ml-2">
    <InformationCircleIcon className="inline h-5 w-5 text-indigo-400 cursor-pointer" />
    <span className="absolute bottom-full mb-2 w-48 bg-indigo-700 text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 pointer-events-none transition-opacity">
      {text}
    </span>
  </span>
);

const CheckInPage = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [passportNumber, setPassportNumber] = useState("");
  const [luggageOption, setLuggageOption] = useState("");
  const [checkInStatus, setCheckInStatus] = useState<string | null>(null);

  const handleCheckIn = async (e: React.FormEvent) => {
    e.preventDefault();

    const clientId = localStorage.getItem("idClient");
    const numeClient = localStorage.getItem("numeClient");
    const prenumeClient = localStorage.getItem("prenumeClient");

    if (!flightNumber || !passportNumber || !luggageOption) {
      setCheckInStatus("⚠️ Te rugăm să completezi toate câmpurile.");
      return;
    }

    if (!clientId) {
      setCheckInStatus("❌ Trebuie să fii logat pentru check-in.");
      return;
    }

    try {
      const response = await fetch("https://localhost:7239/api/CheckIn", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          IDZbor: parseInt(flightNumber),
          IDClient: parseInt(clientId),
          NrPasaport: passportNumber,
          TipBagaj: luggageOption,
          CheckedIN: true,
        }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText);
      }

      setCheckInStatus(
        `✅ Check-in reușit! ${prenumeClient} ${numeClient}, zborul tău a fost înregistrat.`
      );
      setFlightNumber("");
      setPassportNumber("");
      setLuggageOption("");
    } catch (error) {
      console.error(error);
      setCheckInStatus(
        "❌ A apărut o eroare la check-in. Te rugăm să încerci din nou."
      );
    }
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-tr from-blue-200 via-indigo-100 to-pink-100 flex justify-center items-center px-4 py-12 overflow-hidden">
      {/* Background image blur */}
      <img
        src="/airport-background.jpg" // pune o imagine relevantă în folderul public
        alt="Airport background"
        className="absolute inset-0 w-full h-full object-cover filter blur-lg opacity-30 -z-10"
      />

      <div className="bg-white rounded-3xl shadow-2xl max-w-md w-full p-10 relative z-10">
        <h2 className="text-4xl font-bold mb-8 text-center text-indigo-700 drop-shadow-md">
          Check-in Zbor
        </h2>

        <form onSubmit={handleCheckIn} className="space-y-8">
          <div>
            <label
              htmlFor="flightNumber"
              className="block mb-2 text-lg font-semibold text-indigo-600 flex items-center"
            >
              Număr Zbor
              <Tooltip text="Numărul zborului pe care îl găsești pe bilet sau pe rezervare." />
            </label>
            <input
              type="number"
              id="flightNumber"
              value={flightNumber}
              onChange={(e) => setFlightNumber(e.target.value)}
              placeholder="Ex: 1234"
              required
              className="w-full rounded-xl border border-indigo-300 px-5 py-3 text-gray-700 placeholder-indigo-400
                         focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:border-indigo-600
                         transition duration-300"
            />
          </div>

          <div>
            <label
              htmlFor="passportNumber"
              className="block mb-2 text-lg font-semibold text-indigo-600 flex items-center"
            >
              Număr Pașaport
              <Tooltip text="Numărul pașaportului tău, formatul tip AB1234567." />
            </label>
            <input
              type="text"
              id="passportNumber"
              value={passportNumber}
              onChange={(e) => setPassportNumber(e.target.value)}
              placeholder="Ex: AB1234567"
              required
              className="w-full rounded-xl border border-indigo-300 px-5 py-3 text-gray-700 placeholder-indigo-400
                         focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:border-indigo-600
                         transition duration-300"
            />
          </div>

          <div>
            <label
              htmlFor="luggage"
              className="block mb-2 text-lg font-semibold text-indigo-600 flex items-center"
            >
              Tip Bagaj
              <Tooltip text="Alege tipul de bagaj pe care îl vei lua cu tine." />
            </label>
            <select
              id="luggage"
              value={luggageOption}
              onChange={(e) => setLuggageOption(e.target.value)}
              required
              className="w-full rounded-xl border border-indigo-300 px-5 py-3 text-gray-700
                         focus:outline-none focus:ring-4 focus:ring-indigo-400 focus:border-indigo-600
                         transition duration-300"
            >
              <option value="" disabled>
                Selectează opțiunea
              </option>
              <option value="Bagaj de cabină">Bagaj de cabină (max. 8kg)</option>
              <option value="Bagaj de cală">Bagaj de cală (max. 23kg)</option>
              <option value="Bagaj suplimentar">Bagaj suplimentar</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-indigo-600 hover:bg-indigo-700 active:bg-indigo-800
                       text-white font-bold py-4 rounded-2xl shadow-lg
                       transition duration-300 ease-in-out focus:outline-none focus:ring-4
                       focus:ring-indigo-400 flex justify-center items-center space-x-3"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth={2}
            >
              <path strokeLinecap="round" strokeLinejoin="round" d="M7 16v-4a4 4 0 014-4h8" />
              <path strokeLinecap="round" strokeLinejoin="round" d="M7 16l-4-4m0 0l4-4m-4 4h18" />
            </svg>
            <span>Check-in</span>
          </button>
        </form>

        {checkInStatus && (
          <div
            className={`mt-8 text-center font-semibold text-lg ${
              checkInStatus.startsWith("✅")
                ? "text-green-700"
                : checkInStatus.startsWith("⚠️")
                ? "text-yellow-700"
                : "text-red-700"
            }`}
          >
            {checkInStatus}
          </div>
        )}
      </div>
    </div>
  );
};

export default CheckInPage;
