import { useState } from "react";

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    prenume: "",
    nume: "",
    email: "",
    parola: "",
  });
  const [errorMsg, setErrorMsg] = useState("");

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErrorMsg("");

    // Verificare câmpuri goale
    if (
      !formData.prenume.trim() ||
      !formData.nume.trim() ||
      !formData.email.trim() ||
      !formData.parola.trim()
    ) {
      setErrorMsg("Prenume, nume, email, parola sunt obligatorii!");
      return;
    }

    try {
      const response = await fetch("https://localhost:7239/api/Client", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          EmailClient: formData.email,
          ParolaClient: formData.parola,
          NumeClient: formData.nume,
          PrenumeClient: formData.prenume,
        }),
      });

      if (response.status === 400) {
        const text = await response.text();
        setErrorMsg(text);
        return;
      }

      if (response.status === 409) {
        setErrorMsg("Email-ul există deja!");
        return;
      }

      if (!response.ok) {
        setErrorMsg("Eroare la înregistrare!");
        return;
      }

      alert("Înregistrare reușită!");
    } catch (err) {
      console.error(err);
      setErrorMsg("Eroare la înregistrare!");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-6 bg-gradient-to-tr from-green-200 to-blue-300">
      <div className="bg-white rounded-3xl shadow-2xl p-10 max-w-md w-full">
        <h2 className="text-4xl font-extrabold mb-8 text-center text-green-700">
          Înregistrare Client
        </h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <input
            name="prenume"
            placeholder="Prenume"
            className="w-full p-4 border border-gray-300 rounded-lg"
            onChange={handleChange}
          />
          <input
            name="nume"
            placeholder="Nume"
            className="w-full p-4 border border-gray-300 rounded-lg"
            onChange={handleChange}
          />
          <input
            name="email"
            type="email"
            placeholder="Email"
            className="w-full p-4 border border-gray-300 rounded-lg"
            onChange={handleChange}
          />
          <input
            name="parola"
            type="password"
            placeholder="Parolă"
            className="w-full p-4 border border-gray-300 rounded-lg"
            onChange={handleChange}
          />

          {errorMsg && (
            <p className="text-red-600 font-semibold text-center">{errorMsg}</p>
          )}

          <button
            type="submit"
            className="w-full py-3 bg-green-600 text-white rounded-xl font-semibold hover:bg-green-700 transition duration-300"
          >
            Înregistrează-te
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
