import React, { useState } from "react";

type Flight = {
  id: number;
  from: string;
  to: string;
  date: string;
  seats: number;
};

type FlightSearchProps = {
  onSearch: (query: string) => void;
};

const FlightSearch = ({ onSearch }: FlightSearchProps) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [flights, setFlights] = useState<Flight[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fetch flights
  const fetchFlights = async (query: string) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(
        `https://localhost:7239/api/Rute?search=${encodeURIComponent(query)}`
      );
      if (!response.ok) throw new Error("Failed to fetch flights");

      const data = await response.json();
      console.log("Date primite de la backend:", data);

      const mappedFlights = data.map((f: any) => ({
        id: f.idRute,
        from: f.aeroportPlecare || "Necunoscut",
        to: f.aeroportSosire || "Necunoscut",
        date: f.dataOraPlecare
          ? new Date(f.dataOraPlecare).toISOString().split("T")[0]
          : "",
        seats: f.nrMaximLocur,
      }));

      setFlights(mappedFlights);
    } catch (err: any) {
      console.error(err);
      setError(err.message || "A apărut o eroare");
    } finally {
      setLoading(false);
    }
  };

  // Căutare zbor
  const handleSearch = () => {
    onSearch(searchTerm);
    fetchFlights(searchTerm);
  };

  // Rezervare
  const reserveFlight = async (flight: Flight) => {
    const clientId = localStorage.getItem("clientId");
    if (!clientId) {
      alert("Trebuie să fii logat pentru a face o rezervare.");
      return;
    }

    const response = await fetch("https://localhost:7239/api/Rezervari", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        IDRutaRezervare: flight.id,
        IDClientRezervare: Number(clientId),
        OraRezervare: new Date().toISOString(),
      }),
    });

    const responseText = await response.text();
    console.log("Response status:", response.status);
    console.log("Response body:", responseText);

    if (!response.ok) throw new Error("Reservation failed");

    alert(
      `Rezervarea pentru zborul ${flight.from} → ${flight.to} a fost făcută cu succes!`
    );
  };

  return (
    <div className="space-y-8 max-w-6xl mx-auto mt-10">

      {/* Search bar */}
      <div className="flex gap-4">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Ex: Roma"
          className="flex-grow border px-4 py-2 rounded-lg"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition"
          disabled={loading}
        >
          {loading ? "Se caută..." : "Caută"}
        </button>
      </div>

      {error && <p className="text-red-600">{error}</p>}

      {/* Listare zboruri */}
      <div className="grid gap-6">
        {flights.length > 0 ? (
          flights.map((flight) => (
            <div
              key={flight.id}
              className="bg-gray-100 p-4 rounded-xl shadow flex justify-between items-center"
            >
              <div>
                <h3 className="text-lg font-semibold">
                  {flight.from} → {flight.to}
                </h3>
                <p className="text-sm text-gray-600">Plecare: {flight.date}</p>
                <p className="text-md font-bold mt-1">Locuri: {flight.seats}</p>
              </div>
              <button
                onClick={() => reserveFlight(flight)}
                className="bg-green-600 text-white px-5 py-2 rounded-lg hover:bg-green-700 transition"
              >
                Rezervă acum
              </button>
            </div>
          ))
        ) : (
          <p className="text-gray-600">Nu au fost găsite zboruri.</p>
        )}
      </div>
    </div>
  );
};

export default FlightSearch;
