// src/components/FlightSearch.tsx
import React, { useState } from "react";

type Flight = {
  id: number;
  from: string;
  to: string;
  date: string;
  price: string;
};

type FlightSearchProps = {
  onSearch: (query: string) => void;
};

const dummyFlights: Flight[] = [
  { id: 1, from: "București", to: "Roma", date: "2025-06-10", price: "99€" },
  { id: 2, from: "Cluj", to: "Barcelona", date: "2025-06-15", price: "120€" },
  { id: 3, from: "Timișoara", to: "Paris", date: "2025-06-12", price: "110€" },
];

const FlightSearch = ({ onSearch }: FlightSearchProps) => {
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = () => {
    onSearch(searchTerm);
  };

  return (
    <div className="space-y-6">
      {/* Input search */}
      <div className="flex gap-4">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Ex: Roma"
          className="flex-grow border px-4 py-2 rounded-lg"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition"
        >
          Caută
        </button>
      </div>

      {/* Rezultate */}
      <div className="grid gap-6">
        {dummyFlights
          .filter(
            (flight) =>
              flight.to.toLowerCase().includes(searchTerm.toLowerCase()) ||
              flight.from.toLowerCase().includes(searchTerm.toLowerCase())
          )
          .map((flight) => (
            <div
              key={flight.id}
              className="bg-gray-100 p-4 rounded-xl shadow flex justify-between items-center"
            >
              <div>
                <h3 className="text-lg font-semibold">
                  {flight.from} → {flight.to}
                </h3>
                <p className="text-sm text-gray-600">Plecare: {flight.date}</p>
                <p className="text-md font-bold mt-1">Preț: {flight.price}</p>
              </div>
              <button
                onClick={() =>
                  alert(`Zbor rezervat: ${flight.from} → ${flight.to}`)
                }
                className="bg-green-600 text-white px-5 py-2 rounded-lg hover:bg-green-700 transition"
              >
                Rezervă acum
              </button>
            </div>
          ))}
      </div>
    </div>
  );
};

export default FlightSearch;
