import { useState } from "react";

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const [errors, setErrors] = useState({
    name: false,
    email: false,
    subject: false,
    message: false,
  });

  const validate = () => {
    const newErrors = {
      name: formData.name.trim() === "",
      email: !/\S+@\S+\.\S+/.test(formData.email),
      subject: formData.subject.trim() === "",
      message: formData.message.trim() === "",
    };
    setErrors(newErrors);
    return !Object.values(newErrors).some(Boolean);
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: false }); // reset error on change
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validate()) {
      console.log(formData);
      alert("Mesaj trimis cu succes!");
      // reset form or API call here
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 px-4 py-8">
      <h2 className="text-3xl font-bold mb-6">ContacteazÄƒ-ne</h2>
      <form
        onSubmit={handleSubmit}
        className="w-full max-w-md space-y-4 bg-white p-6 rounded-xl shadow"
        noValidate
      >
        <div>
          <input
            type="text"
            name="name"
            placeholder="Nume"
            value={formData.name}
            onChange={handleChange}
            aria-invalid={errors.name ? "true" : "false"}
            className={`w-full p-3 border rounded ${
              errors.name ? "border-red-500" : "border-gray-300"
            }`}
            required
          />
          {errors.name && (
            <p className="text-red-600 text-sm mt-1">Numele este obligatoriu.</p>
          )}
        </div>

        <div>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            aria-invalid={errors.email ? "true" : "false"}
            className={`w-full p-3 border rounded ${
              errors.email ? "border-red-500" : "border-gray-300"
            }`}
            required
          />
          {errors.email && (
            <p className="text-red-600 text-sm mt-1">Email invalid.</p>
          )}
        </div>

        <div>
          <input
            type="text"
            name="subject"
            placeholder="Subiect"
            value={formData.subject}
            onChange={handleChange}
            aria-invalid={errors.subject ? "true" : "false"}
            className={`w-full p-3 border rounded ${
              errors.subject ? "border-red-500" : "border-gray-300"
            }`}
            required
          />
          {errors.subject && (
            <p className="text-red-600 text-sm mt-1">Subiectul este obligatoriu.</p>
          )}
        </div>

        <div>
          <textarea
            name="message"
            placeholder="Mesaj"
            value={formData.message}
            onChange={handleChange}
            aria-invalid={errors.message ? "true" : "false"}
            className={`w-full p-3 border rounded ${
              errors.message ? "border-red-500" : "border-gray-300"
            }`}
            required
          />
          {errors.message && (
            <p className="text-red-600 text-sm mt-1">Mesajul este obligatoriu.</p>
          )}
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Trimite Mesajul
        </button>
      </form>
    </div>
  );
};

export default ContactPage;
