import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav className="w-full fixed top-0 left-0 z-50 bg-white shadow-md">
      <div className="max-w-5xl mx-auto flex items-center justify-center gap-4 py-4 px-6">
        <h1 className="text-2xl font-bold text-blue-900 mr-6 select-none">✈️ AirSwift</h1>
        <div className="flex flex-wrap justify-center gap-3">
          {pages.map(({ name, path }) => {
            const isActive = location.pathname === path;
            return (
              <button
                key={path}
                onClick={() => navigate(path)}
                className={`
                  px-4 py-2 rounded-md font-semibold text-sm transition
                  ${
                    isActive
                      ? "bg-gradient-to-r from-blue-600 to-cyan-500 text-white shadow-lg"
                      : "bg-white text-blue-700 hover:bg-blue-600 hover:text-white"
                  }
                  focus:outline-none focus:ring-2 focus:ring-blue-400
                `}
              >
                {name}
              </button>
            );
          })}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
