// src/components/Navbar.tsx
import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <div className="h-screen w-full bg-blue-700 flex flex-col justify-center items-center text-center px-6">
      {/* Logo */}
      <h1 className="text-4xl font-bold text-white mb-8">✈️ AirSwift</h1>

      {/* Buttons */}
      <div className="flex flex-wrap justify-center gap-4 max-w-3xl">
        {pages.map(({ name, path }) => (
          <button
            key={path}
            onClick={() => navigate(path)}
            className={`px-6 py-3 rounded-full font-medium transition-all duration-200 text-lg
              ${
                location.pathname === path
                  ? "bg-white text-blue-700 shadow-lg"
                  : "bg-blue-600 text-white hover:bg-white hover:text-blue-700 hover:shadow-md"
              }`}
          >
            {name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Navbar;
