// src/components/Navbar.tsx
import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav className="bg-blue-700 shadow-md fixed top-0 left-0 right-0 z-50">
      <div className="max-w-7xl mx-auto px-6 py-4 flex flex-col md:flex-row items-center justify-center gap-4">
        {/* Logo */}
        <h1 className="text-3xl font-bold text-white mb-2 md:mb-0 md:mr-6">
          ✈️ AirSwift
        </h1>

        {/* Navigation buttons */}
        <div className="flex flex-wrap justify-center gap-3">
          {pages.map(({ name, path }) => (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={`px-4 py-2 rounded-full font-medium transition-all duration-200 text-sm md:text-base 
                ${
                  location.pathname === path
                    ? "bg-white text-blue-700 shadow-lg"
                    : "bg-blue-600 text-white hover:bg-white hover:text-blue-700 hover:shadow-md"
                }`}
            >
              {name}
            </button>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
