import { useNavigate } from "react-router-dom";

const AboutPage = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-sky-100 to-indigo-200 px-4 py-16">
      <h2 className="text-5xl font-extrabold mb-12 text-gray-800 drop-shadow-md text-center">
        Despre Noi
      </h2>

      <div className="bg-white/90 p-10 rounded-3xl shadow-xl max-w-4xl w-full space-y-10 border border-gray-200 backdrop-blur-md">
        <Section
          title="Cum a început totul"
          content="Ideea acestui proiect a luat naștere dintr-o echipă mică, dar ambițioasă de 5 oameni care aveau o viziune clară: să revoluționeze industria turismului. În 2020, am hotărât să facem un pas curajos și să transformăm această idee într-un proiect real, cu scopul de a oferi clienților nu doar o experiență de călătorie, ci o adevărată aventură."
        />
        <Section
          title="Cum ne facem remarcați"
          content="De la înființare, am muncit neîncetat pentru a ne construi o reputație solidă. Astăzi, suntem deja un nume pe piață, iar succesul nostru se datorează abordării inovative și angajamentului față de clienți. Ce ne diferențiază? Profesionalismul, prețurile competitive și condițiile flexibile care se potrivesc oricărei nevoi."
        />
        <Section
          title="Echipa Noastră"
          content="Suntem o echipă dedicată, cu diverse abilități și pasiuni comune pentru turism și tehnologie. Fiecare membru al echipei aduce un set unic de abilități care ne ajută să înfruntăm orice provocare. De la design-ul interfeței, la analiza pieței și relațiile cu clienții, fiecare pas este gândit pentru a oferi cele mai bune soluții și experiențe."
        />
        <Section
          title="De ce să alegi serviciile noastre?"
          content="Alege-ne pentru că îți oferim mai mult decât o simplă vacanță. Oferim experiențe personalizate, în funcție de nevoile tale, și prețuri care îți permit să călătorești fără să îți faci griji de buget. Suntem alături de tine în fiecare pas al călătoriei tale!"
        />
      </div>

      <button
        onClick={() => navigate("/")}
        className="mt-14 bg-indigo-600 text-white text-xl font-semibold py-3 px-8 rounded-full hover:bg-indigo-700 transition-all duration-300 shadow-lg"
      >
        ⬅ Înapoi la Home
      </button>
    </div>
  );
};

const Section = ({
  title,
  content,
}: {
  title: string;
  content: string;
}) => (
  <section className="space-y-2">
    <h3 className="text-2xl font-semibold text-gray-900">{title}</h3>
    <p className="text-gray-700 leading-relaxed text-lg">{content}</p>
  </section>
);

export default AboutPage;
