import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  const secretSequence = ["/", "/contact", "/profile", "/about"];
  const [sequenceIndex, setSequenceIndex] = useState(0);

  const handleNavClick = (path: string) => {
    if (path === secretSequence[sequenceIndex]) {
      const nextIndex = sequenceIndex + 1;
      setSequenceIndex(nextIndex);
      if (nextIndex === secretSequence.length) {
        navigate("/secret");
        setSequenceIndex(0);
      }
    } else {
      setSequenceIndex(path === secretSequence[0] ? 1 : 0);
    }

    navigate(path);
  };

  return (
    <nav className="w-full absolute top-4 left-0 z-50">
      <div className="w-full flex flex-col items-center">
        <h1
          className="text-2xl font-bold text-blue-900 mb-2 cursor-pointer"
        >
          ✈️ AirSwift
        </h1>
        <div className="flex flex-wrap justify-center gap-2 bg-sky-200/90 px-6 py-2 rounded-md shadow-md">
          {pages.map(({ name, path }) => (
            <button
              key={path}
              onClick={() => handleNavClick(path)}
              className={`px-3 py-1 rounded-md font-medium transition text-sm
                ${
                  location.pathname === path
                    ? "bg-blue-700 text-white"
                    : "bg-white text-blue-700 hover:bg-blue-700 hover:text-white"
                }`}
            >
              {name}
            </button>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
