import React from "react";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";

const offers = [
  {
    title: "Vacanță în Grecia",
    price: "De la 199€ pe persoană",
    image: "/images/grecia.jpg",
    bgColor: "from-pink-600 to-pink-400",
  },
  {
    title: "Vacanță în Italia",
    price: "De la 250€ pe persoană",
    image: "/images/italia.jpg",
    bgColor: "from-green-700 to-green-400",
  },
  {
    title: "Vacanță în Spania",
    price: "De la 199€ pe persoană",
    image: "/images/spania.jpg",
    bgColor: "from-blue-700 to-blue-400",
  },
  {
    title: "Vacanță în Franța",
    price: "De la 280€ pe persoană",
    image: "/images/franta.jpg",
    bgColor: "from-yellow-600 to-yellow-400",
  },
  {
    title: "Vacanță în Portugalia",
    price: "De la 230€ pe persoană",
    image: "/images/portugalia.jpg",
    bgColor: "from-red-600 to-red-400",
  },
];

const HomePage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-white">
      <Navbar />

      <main className="max-w-7xl mx-auto p-8">
        <h2 className="text-5xl font-extrabold mb-16 text-center text-gray-900 drop-shadow-md">
          Oferte de sezon
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-12">
          {offers.map((offer, index) => (
            <div
              key={index}
              className={`relative rounded-3xl overflow-hidden shadow-2xl group cursor-pointer transform hover:scale-[1.03] transition-transform duration-300`}
            >
              {/* Background Gradient Overlay */}
              <div
                className={`absolute inset-0 bg-gradient-to-br ${offer.bgColor} opacity-80 z-0`}
                aria-hidden="true"
              ></div>

              {/* Image */}
              <div className="relative z-10 overflow-hidden rounded-3xl h-64">
                <img
                  src={offer.image}
                  alt={offer.title}
                  className="w-full h-full object-cover object-center transition-transform duration-500 group-hover:scale-110"
                />
              </div>

              {/* Content */}
              <div className="absolute bottom-0 left-0 right-0 p-6 bg-black bg-opacity-50 z-20 rounded-b-3xl text-white">
                <h3 className="text-3xl font-semibold drop-shadow-lg mb-2">
                  {offer.title}
                </h3>
                <p className="text-xl mb-4 drop-shadow-md">{offer.price}</p>
                <Link
                  to="/flights"
                  className="inline-block bg-white text-gray-900 font-semibold px-6 py-3 rounded-full shadow-lg hover:bg-gray-100 focus:outline-none focus:ring-4 focus:ring-gray-300 transition"
                >
                  Rezervă acum!
                </Link>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
