import { useNavigate, useLocation } from "react-router-dom";
import React from "react";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav className="fixed inset-0 flex flex-col items-center justify-center bg-white/95 z-50 font-sans">
      <h1 className="text-3xl font-bold text-blue-900 mb-6 opacity-0 animate-fadeInUp">
        ✈️ AirSwift
      </h1>
      <div className="flex flex-wrap justify-center items-center gap-4 px-8 py-3 rounded-lg shadow-lg bg-white/95">
        {pages.map(({ name, path }, index) => (
          <React.Fragment key={path}>
            <button
              onClick={() => navigate(path)}
              className={`px-4 py-2 rounded-md font-semibold transition duration-300 ease-in-out transform hover:scale-105 text-sm shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-1
                ${
                  location.pathname === path
                    ? "bg-blue-700 text-white shadow-md"
                    : "bg-white text-blue-700 hover:bg-blue-700 hover:text-white hover:shadow-md"
                }`}
            >
              {name}
            </button>
            {index < pages.length - 1 && (
              <span className="hidden md:inline-block mx-2 text-gray-400 select-none">
                •
              </span>
            )}
          </React.Fragment>
        ))}
      </div>
    </nav>
  );
};

export default Navbar;
