import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Register from "../components/Login"; // Importul componentei Register din Login.tsx (poți redenumi fișierul la Register.tsx pentru claritate)

const LoginPage = () => {
  // State login
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  // Login handler
  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "https://localhost:5001/api/pasageri/login",
        {
          email,
          password,
        }
      );
      const user = response.data;
      console.log("Autentificat:", user);
      navigate("/profile");
    } catch (err) {
      console.error(err);
      alert("Credențiale greșite!");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4 bg-gradient-to-r from-blue-500 to-teal-500 space-y-12">
      {/* Login form */}
      <div className="bg-white p-8 rounded-lg shadow-xl max-w-sm w-full">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">
          Autentificare Pasager
        </h2>
        <form onSubmit={handleLogin} className="space-y-6">
          <input
            type="email"
            placeholder="Email"
            className="w-full p-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Parolă"
            className="w-full p-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition duration-300"
          >
            Autentifică-te
          </button>
        </form>
        <div className="mt-4 text-center">
          <p className="text-sm text-gray-600">
            Nu ai cont?{" "}
            <a href="#register" className="text-blue-600 hover:underline">
              Înregistrează-te aici
            </a>
          </p>
        </div>
      </div>

      {/* Register component */}
      <Register />
    </div>
  );
};

export default LoginPage;
