import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";

const offers = [
  {
    title: "Vacan»õƒÉ √Æn Grecia",
    price: "De la 199‚Ç¨ pe persoanƒÉ",
    image: "/images/grecia.jpg",
    bgColor: "bg-pink-600",
  },
  {
    title: "Vacan»õƒÉ √Æn Italia",
    price: "De la 250‚Ç¨ pe persoanƒÉ",
    image: "/images/italia.jpg",
    bgColor: "bg-green-600",
  },
  {
    title: "Vacan»õƒÉ √Æn Spania",
    price: "De la 199‚Ç¨ pe persoanƒÉ",
    image: "/images/spania.jpg",
    bgColor: "bg-blue-600",
  },
  {
    title: "Vacan»õƒÉ √Æn Fran»õa",
    price: "De la 280‚Ç¨ pe persoanƒÉ",
    image: "/images/franta.jpg",
    bgColor: "bg-yellow-600",
  },
  {
    title: "Vacan»õƒÉ √Æn Portugalia",
    price: "De la 230‚Ç¨ pe persoanƒÉ",
    image: "/images/portugalia.jpg",
    bgColor: "bg-red-600",
  },
];

const emojis = ["\uD83C\uDFD6\uFE0F", "\uD83D\uDD76\uFE0F", "\uD83C\uDF79", "\uD83D\uDC52", "\uD83D\uDEA4"];

const Footer = () => {
  return (
    <footer className="bg-gray-900 text-gray-300 py-12 px-6 mt-20">
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-start gap-12">
        <div className="flex flex-col space-y-4">
          <div className="text-white text-3xl font-extrabold tracking-wide">AirSwift</div>
          <p className="text-gray-500 text-sm">&copy; 2025 AirSwift. Toate drepturile rezervate üòé .</p>
        </div>

        <nav className="flex flex-col space-y-3 text-sm text-gray-400">
          <Link to="/" className="hover:text-white transition">AcasƒÉ</Link>
          <Link to="/contact" className="hover:text-white transition">Contact</Link>
          <Link to="/about" className="hover:text-white transition">Despre noi</Link>
        </nav>

        <div className="flex flex-col space-y-3 text-gray-400">
          <div className="font-semibold text-white">UrmƒÉre»ôte-ne</div>
          <div className="flex space-x-6 text-2xl">
            <a href="#" aria-label="Facebook" className="hover:text-white transition">üìò</a>
            <a href="#" aria-label="Twitter" className="hover:text-white transition">üê¶</a>
            <a href="#" aria-label="Instagram" className="hover:text-white transition">üì∏</a>
          </div>
        </div>

        <div className="flex flex-col space-y-3 max-w-xs w-full">
          <label htmlFor="email" className="font-semibold text-white tracking-wide">AboneazƒÉ-te la newsletter</label>
          <div className="flex">
            <input
              id="email"
              type="email"
              placeholder="Adresa ta de email"
              className="flex-grow rounded-l-lg p-3 text-gray-900 focus:outline-none"
            />
            <button className="bg-yellow-400 text-gray-900 px-6 rounded-r-lg font-semibold hover:bg-yellow-500 transition">
              AboneazƒÉ-te
            </button>
          </div>
        </div>
      </div>
    </footer>
  );
};

const HomePage = () => {
  const [heldIndex, setHeldIndex] = useState<number | null>(null);
  const timeoutRefs = useRef<(ReturnType<typeof setTimeout> | null)[]>(Array(offers.length).fill(null));

  const handleMouseDown = (index: number) => {
    timeoutRefs.current[index] = setTimeout(() => {
      setHeldIndex(index);
    }, 3000);
  };

  const handleMouseUp = (index: number) => {
    if (timeoutRefs.current[index]) {
      clearTimeout(timeoutRefs.current[index]!);
      timeoutRefs.current[index] = null;
    }
  };

  return (
    <div className="min-h-screen bg-white flex flex-col">
      <main className="p-8 flex-grow max-w-7xl mx-auto w-full">
        <h2 className="text-4xl font-bold mb-12 text-center text-gray-800">Oferte de sezon</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-10" style={{ perspective: "1000px" }}>
          {offers.map((offer, index) => (
            <div
              key={index}
              onMouseDown={() => handleMouseDown(index)}
              onMouseUp={() => handleMouseUp(index)}
              onTouchStart={() => handleMouseDown(index)}
              onTouchEnd={() => handleMouseUp(index)}
              className={`relative rounded-2xl shadow-2xl overflow-hidden cursor-pointer transform transition-transform duration-300 hover:scale-[1.05] hover:shadow-3xl ${offer.bgColor}`}
              style={{
                animation: `fadeInUp 0.5s ease forwards`,
                animationDelay: `${index * 0.15}s`,
                opacity: 0,
              }}
            >
              {heldIndex === index && (
                <div className="absolute top-3 right-3 text-[6rem] z-30">{emojis[index]}</div>
              )}

              <div className="absolute inset-0 bg-gradient-to-t from-black/70 to-transparent z-10"></div>

              <div className="relative z-20 p-8 flex flex-col items-center text-center text-white">
                <h3 className="text-3xl font-semibold mb-3 drop-shadow-md">{offer.title}</h3>
                <p className="text-xl mb-6 drop-shadow-md">{offer.price}</p>
                <Link
                  to="/flights"
                  className="bg-white text-black font-semibold px-8 py-3 rounded-full hover:bg-opacity-90 transition shadow-md hover:shadow-lg active:scale-95"
                >
                  RezervƒÉ acum!
                </Link>
              </div>

              <img
                src={offer.image}
                alt={offer.title}
                className="w-full h-[260px] object-cover rounded-2xl"
              />
            </div>
          ))}
        </div>
      </main>

      <Footer />

      <style>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default HomePage;
