import React from 'react';

const Home: React.FC = () => {
  const games = [
    { src: '/images/blackjack.jpg', alt: 'Blackjack', name: 'Blackjack' },
    { src: '/images/poker-table.jpg', alt: 'Poker', name: 'Poker' },
    { src: '/images/roulette-wheel.jpg', alt: 'Ruletă', name: 'Ruletă' },
    { src: '/images/slot-machine.jpg', alt: 'Slot Machine', name: 'Slot Machine' },
  ];

  return (
    <div
      className="text-white min-h-screen flex flex-col items-center justify-center px-6"
      style={{
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
      }}
    >
      <header className="mb-10 flex items-center space-x-4">
        <img
          src="/images/casino-logo192.png"
          alt="Casino Logo"
          className="w-60 h-60 rounded"
        />
        <h1 className="text-5xl font-bold drop-shadow-lg max-w-xl">
          Bun venit la Casino Online
        </h1>
      </header>

      <p className="text-lg max-w-xl text-center drop-shadow-md mb-12 px-4">
        Joacă cele mai tari jocuri de cazino: Blackjack, Poker, Ruletă, Slot Machines și multe altele!
      </p>

      {/* Aici containerul flex pentru toate imaginile pe un singur rând */}
      <div className="flex flex-row space-x-12 overflow-x-auto w-full max-w-5xl">
        {games.map((game) => (
          <div key={game.name} className="flex flex-col items-center min-w-[200px]">
            <h3 className="mb-4 text-2xl font-semibold drop-shadow-md">{game.name}</h3>
            <img
              src={game.src}
              alt={game.alt}
              className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer w-48 h-36 object-cover"
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
