import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../AuthContext'; // ajusteazÄƒ calea

const Navbar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { isLoggedIn, logout } = useAuth();

  // Definim toate paginile
  const allPages = [
    { name: 'Home', path: '/' },
    { name: 'Pariuri', path: '/pariuri' },
    { name: 'Jocuri', path: '/jocuri' },
    { name: 'Tranzactii', path: '/tranzactii' },
    { name: 'Deposit', path: '/deposit' },
    { name: 'Profil', path: '/profil' },
    { name: 'Login', path: '/login' },
    { name: 'Register', path: '/register' },
  ];

  // DacÄƒ nu e logat, aratÄƒ doar Home, Jocuri, Login, Register
  const guestPages = allPages.filter(p =>
    ['Home', 'Jocuri', 'Login', 'Register'].includes(p.name)
  );

  // DacÄƒ e logat, aratÄƒ toate mai puÈ›in Login
  const userPages = allPages.filter(p => p.name !== 'Login');

  // Alege ce sÄƒ afiÈ™ezi Ã®n funcÈ›ie de login
  const pagesToShow = isLoggedIn ? userPages : guestPages;

  return (
    <nav style={{ /* stilurile tale */ }}>
      <h1 style={{ /* stilurile tale */ }}>ðŸŽ° CasinoApp</h1>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.75rem', justifyContent: 'center' }}>
        {pagesToShow.map(({ name, path }) => {
          const isActive = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              title={`NavigheazÄƒ la ${name}`}
              style={{
                padding: '0.6rem 1.2rem',
                borderRadius: '6px',
                fontWeight: 600,
                fontSize: '0.95rem',
                cursor: 'pointer',
                border: '1px solid #ffcc00',
                backgroundColor: isActive ? '#ffcc00' : 'transparent',
                color: isActive ? '#1a1a1a' : '#f0f0f0',
                transition: 'all 0.3s ease',
              }}
            >
              {name}
            </button>
          );
        })}

        {/* DacÄƒ e logat, afiÈ™Äƒm È™i butonul Logout */}
        {isLoggedIn && (
          <button
            onClick={() => {
              logout();
              navigate('/login');
            }}
            style={{
              padding: '0.6rem 1.2rem',
              borderRadius: '6px',
              fontWeight: 600,
              fontSize: '0.95rem',
              cursor: 'pointer',
              border: '1px solid #ffcc00',
              backgroundColor: 'transparent',
              color: '#f0f0f0',
              transition: 'all 0.3s ease',
            }}
            title="DeconecteazÄƒ-te"
          >
            Logout
          </button>
        )}
      </div>

      <footer style={{ /* stilurile tale */ }}>
        Â© 2025 CasinoApp
      </footer>
    </nav>
  );
};

export default Navbar;
