import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Dacă user este null => nu e logat
  // Dacă user are valoare => e logat
  const user = {
    username: 'StefanR',
    avatar: '/avatars/user2.png',
  };
  // Dacă vrei să testezi ce se întâmplă când nu e logat, fă user = null

  // Toate paginile fără Deposit
  const commonPages = [
    { name: 'Home', path: '/' },
    { name: 'Pariuri', path: '/pariuri' },
    { name: 'Jocuri', path: '/jocuri' },
  ];

  // Paginile pentru utilizator neautentificat
  const guestPages = [
    { name: 'Login', path: '/login' },
    { name: 'Register', path: '/register' },
  ];

  // Paginile pentru utilizator autentificat
  const userPages = [
    { name: 'Tranzactii', path: '/tranzactii' },
    { name: 'Profil', path: '/profil' },
  ];

  // Construim lista de pagini afișate
  const pages = user
    ? [...commonPages, ...userPages]
    : [...commonPages, ...guestPages];

  return (
    <>
      <style>{`
        /* codul tău CSS rămâne la fel */
      `}</style>

      <nav
        style={{
          position: 'relative',
          padding: '1rem',
          background: `linear-gradient(
            135deg,
            rgba(10, 10, 30, 0.95),
            rgba(20, 20, 60, 0.95),
            rgba(30, 30, 80, 0.95)
          )`,
          backgroundBlendMode: 'overlay',
          backgroundImage:
            'repeating-linear-gradient(45deg, rgba(255,255,255,0.02) 0px, rgba(255,255,255,0.02) 1px, transparent 1px, transparent 4px)',
          boxShadow: '0 2px 12px rgba(0,0,0,0.4)',
          color: '#f0f0f0',
          userSelect: 'none',
          borderBottom: '2px solid #ffcc00',
        }}
      >
        <h1 className="betonro-title">BetonRO</h1>

        {/* User info colț dreapta sus */}
        {user && (
          <div className="user-info" title={`Logged in as ${user.username}`}>
            <div className="avatar-circle" aria-label="User avatar">
              {user.avatar ? (
                <img src={user.avatar} alt={`${user.username} avatar`} />
              ) : (
                user.username.charAt(0).toUpperCase()
              )}
            </div>
            <div>{user.username}</div>
          </div>
        )}

        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: '0.75rem',
            justifyContent: 'center',
          }}
        >
          {pages.map(({ name, path }) => {
            const isActive = location.pathname === path;
            return (
              <button
                key={path}
                onClick={() => navigate(path)}
                title={`Navighează la ${name}`}
                style={{
                  padding: '0.6rem 1.2rem',
                  borderRadius: '6px',
                  fontWeight: 600,
                  fontSize: '0.95rem',
                  cursor: 'pointer',
                  border: '1px solid #ffcc00',
                  backgroundColor: isActive ? '#ffcc00' : 'transparent',
                  color: isActive ? '#1a1a1a' : '#f0f0f0',
                  transition: 'all 0.3s ease',
                }}
              >
                {name}
              </button>
            );
          })}
        </div>

        <footer
          style={{
            marginTop: '1.5rem',
            textAlign: 'center',
            fontSize: '0.8rem',
            color: '#cccccc',
            paddingTop: '0.75rem',
            borderTop: '1px solid #444',
          }}
        >
          © 2025 CasinoApp
        </footer>
      </nav>
    </>
  );
};

export default Navbar;
