import React from 'react';

const Jocuri: React.FC = () => {
  const games = [
    { id: 1, name: 'Ruleta', description: 'Joacă ruleta și încearcă norocul!' },
    { id: 2, name: 'Blackjack', description: 'Încearcă să bați dealerul la Blackjack.' },
    { id: 3, name: 'Poker', description: 'Testează-ți abilitățile la Poker.' },
  ];

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
        paddingTop: '4rem',
        paddingBottom: '4rem',
      }}
    >
      <div className="max-w-4xl w-full p-6 bg-white bg-opacity-90 shadow-md rounded-md">
        <h1 className="text-3xl font-bold mb-6 text-center">Jocuri</h1>
        <ul>
          {games.map((game) => (
            <li key={game.id} className="mb-4 p-4 border border-gray-300 rounded hover:bg-gray-100 cursor-pointer">
              <h2 className="text-xl font-semibold">{game.name}</h2>
              <p>{game.description}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Jocuri;
