// src/components/FlightSearch.tsx
import React, { useState } from "react";
const formatDateTime = (dateString: string) => {
  if (!dateString) return "";

  const isoString = dateString.replace(" ", "T");
  const d = new Date(isoString);
  if (isNaN(d.getTime())) return "Invalid Date";

  const datePart = d.toISOString().split('T')[0];
  const timePart = d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  return `${datePart} ${timePart}`;
};


type Flight = {
  id: number;
  from: string;
  to: string;
  date: string;
  seats: number;
};

type FlightSearchProps = {
  onSearch: (query: string) => void;
};

const FlightSearch = ({ onSearch }: FlightSearchProps) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [flights, setFlights] = useState<Flight[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fetch flights from backend API
  const fetchFlights = async (query: string) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`https://localhost:7239/api/Rute?search=${encodeURIComponent(query)}`);
      if (!response.ok) throw new Error("Failed to fetch flights");

      const data = await response.json();

      // Adapt backend data to Flight type if needed
     
      console.log("Date primite de la backend:", data);
      const mappedFlights = data.map((f: any) => ({
        id: f.IDRute,
        from: f.aeroportPlecare || "Unknown", 
        to: f.aeroportSosire || "Unknown",      
        date: formatDateTime(f.DataOraPlecare),
        seats: f.nrMaximLocur
      }));

      setFlights(mappedFlights);
    } catch (err: any) {
      console.error(err);
      setError(err.message || "An error occurred");
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    onSearch(searchTerm);
    fetchFlights(searchTerm);
  };

  const reserveFlight = async (flight: Flight) => {
    try {
      const clientId = 1; // Replace with actual logged-in user ID

      const response = await fetch("https://localhost:7239/api/Rezervari", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          IDRutaRezervare: flight.id,
          IDClientRezervare: clientId,
          OraRezervare: new Date().toISOString(),
        }),
      });
      if (!response.ok) throw new Error("Reservation failed");

      alert(`Rezervarea pentru zborul ${flight.from} → ${flight.to} a fost făcută cu succes!`);
    } catch (err) {
      console.error(err);
      alert("Eroare la rezervare, încearcă din nou.");
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex gap-4">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Ex: Roma"
          className="flex-grow border px-4 py-2 rounded-lg"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition"
          disabled={loading}
        >
          {loading ? "Se caută..." : "Caută"}
        </button>
      </div>

      {error && <p className="text-red-600">{error}</p>}

      <div className="grid gap-6">
        {flights.length > 0 ? (
          flights.map((flight) => (
            <div
              key={flight.id}
              className="bg-gray-100 p-4 rounded-xl shadow flex justify-between items-center"
            >
              <div>
                <h3 className="text-lg font-semibold">
                  {flight.from} → {flight.to}
                </h3>
                <p className="text-sm text-gray-600">Plecare: {flight.date}</p>
                <p className="text-md font-bold mt-1">Seats: {flight.seats}</p>
              </div>
              <button
                onClick={() => reserveFlight(flight)}
                className="bg-green-600 text-white px-5 py-2 rounded-lg hover:bg-green-700 transition"
              >
                Rezervă acum
              </button>
            </div>
          ))
        ) : (
          <p>Nu au fost găsite zboruri.</p>
        )}
      </div>
    </div>
  );
};

export default FlightSearch;
