import React, { useState } from 'react';

const symbols = ['üçí', 'üçã', 'üçá', 'üîî', 'üíé', 'üëë']; // Simboluri posh de tip Shiny Crown

const generateGrid = (): string[][] => {
  const grid = [];
  for (let row = 0; row < 5; row++) {
    const newRow = [];
    for (let col = 0; col < 5; col++) {
      const randomSymbol = symbols[Math.floor(Math.random() * symbols.length)];
      newRow.push(randomSymbol);
    }
    grid.push(newRow);
  }
  return grid;
};

const SlotMachine: React.FC = () => {
  const [grid, setGrid] = useState<string[][]>(generateGrid());
  const [message, setMessage] = useState('');

  const spin = () => {
    const newGrid = generateGrid();
    setGrid(newGrid);
    setMessage(checkWin(newGrid));
  };

  const checkWin = (grid: string[][]): string => {
    // Exemplu simplu: linie completƒÉ identicƒÉ
    for (let row of grid) {
      if (row.every((symbol) => symbol === row[0])) {
        return `üéâ Linia completƒÉ: ${row[0]}! Ai c√¢»ôtigat!`;
      }
    }
    return '‚ùå Nicio linie completƒÉ. √éncearcƒÉ din nou!';
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #1a1a2e, #3a0ca3)',
        color: '#ffd700',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '2rem',
        fontFamily: '"Segoe UI", sans-serif',
      }}
    >
      <h1 style={{ fontSize: '2.5rem', textShadow: '0 0 10px #ffd700' }}>
        üëë Shiny Crown Slot Machine üé∞
      </h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(5, 80px)',
          gridGap: '10px',
          margin: '2rem 0',
          background: '#111',
          padding: '10px',
          borderRadius: '12px',
          boxShadow: '0 0 20px rgba(255, 215, 0, 0.4)',
        }}
      >
        {grid.map((row, rowIndex) =>
          row.map((symbol, colIndex) => (
            <div
              key={`${rowIndex}-${colIndex}`}
              style={{
                width: '80px',
                height: '80px',
                background: '#222',
                border: '2px solid #ffd700',
                borderRadius: '8px',
                fontSize: '2rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: 'inset 0 0 8px #000',
              }}
            >
              {symbol}
            </div>
          ))
        )}
      </div>

      <button onClick={spin} style={buttonStyle}>
        SPIN üé∞
      </button>

      {message && (
        <div style={{ marginTop: '1rem', fontSize: '20px', fontWeight: 'bold' }}>{message}</div>
      )}
    </div>
  );
};

const buttonStyle: React.CSSProperties = {
  padding: '12px 30px',
  backgroundColor: '#ffd700',
  border: 'none',
  borderRadius: '10px',
  color: '#000',
  fontSize: '18px',
  fontWeight: 'bold',
  cursor: 'pointer',
  boxShadow: '0 0 15px rgba(255, 215, 0, 0.7)',
  transition: 'transform 0.2s ease',
};

export default SlotMachine;
