import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: 'Home', path: '/' },
    { name: 'Pariuri', path: '/pariuri' },
    { name: 'Jocuri', path: '/jocuri' },
    { name: 'Tranzactii', path: '/tranzactii' },
    { name: 'Deposit', path: '/deposit' },
    { name: 'Profil', path: '/profil' },
    { name: 'Login', path: '/login' },
    { name: 'Register', path: '/register' },
  ];

  return (
    <nav
      style={{
        padding: '1rem',
        background: 'linear-gradient(90deg, #add8e6 0%, #87ceeb 50%, #00bfff 100%)',
        boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
        color: '#1a1a1a',
        userSelect: 'none',
      }}
    >
      <h1
        style={{
          marginBottom: '0.75rem',
          color: '#003366',
          textAlign: 'center',
          fontWeight: 'bold',
          fontSize: '2rem',
          cursor: 'default',
          userSelect: 'none',
        }}
      >
        ðŸŽ° CasinoApp
      </h1>

      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '0.75rem',
          justifyContent: 'center',
        }}
      >
        {pages.map(({ name, path }) => {
          const isActive = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              title={`NavigheazÄƒ la ${name}`}
              style={{
                padding: '0.6rem 1.2rem',
                borderRadius: '6px',
                fontWeight: 600,
                fontSize: '0.95rem',
                cursor: 'pointer',
                border: 'none',
                minWidth: '100px',
                backgroundColor: isActive ? '#005999' : '#e0f4ff',
                color: isActive ? '#fff' : '#003366',
                transition: 'all 0.3s ease',
              }}
            >
              {name}
            </button>
          );
        })}
      </div>

      <footer
        style={{
          marginTop: '1.5rem',
          textAlign: 'center',
          fontSize: '0.8rem',
          color: '#003366',
          paddingTop: '0.75rem',
          borderTop: '1px solid #003366',
        }}
      >
        Â© 2025 CasinoApp
      </footer>
    </nav>
  );
};

export default Navbar;
