import React from 'react';

const Home: React.FC = () => {
  const games = [
    { src: '/images/blackjack.jpg', alt: 'Blackjack', name: 'Blackjack' },
    { src: '/images/poker-table.jpg', alt: 'Poker', name: 'Poker' },
    { src: '/images/roulette-wheel.jpg', alt: 'Ruletă', name: 'Ruletă' },
    { src: '/images/slot-machine.jpg', alt: 'Slot Machine', name: 'Slot Machine' },
  ];

  return (
    <div
      style={{
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        textAlign: 'center',
        padding: '20px',
      }}
    >
      {/* Logo + Titlu + Paragraf centrate */}
      <div style={{ marginBottom: '60px' }}>
        <img
          src="/images/casino-logo192.png"
          alt="Casino Logo"
          style={{ width: '80px', height: '80px', borderRadius: '8px', marginBottom: '20px' }}
        />
        <h1
          style={{
            fontSize: '5rem',
            fontWeight: '900',
            letterSpacing: '6px',
            background:
              'linear-gradient(90deg, #00ffff, #0077ff, #00ffff)',
            backgroundSize: '300% 100%',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            color: 'transparent',
            animation: 'gradientText 6s linear infinite',
            textShadow:
              '0 0 8px #00cfff, 0 0 15px #0099ff, 0 0 20px #0077ff',
            marginBottom: '15px',
          }}
        >
          Bun venit la Cazinou BetON – câștiguri garantate!
        </h1>
        <p style={{ fontSize: '1.25rem', maxWidth: '600px', margin: '0 auto' }}>
          Joacă cele mai tari jocuri de cazino: Blackjack, Poker, Ruletă, Slot Machines și multe altele!
        </p>
      </div>

      {/* Jocuri în linie, mărime medie */}
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '40px',
          justifyContent: 'center',
        }}
      >
        {games.map((game) => (
          <div key={game.name} style={{ textAlign: 'center' }}>
            <p style={{ marginBottom: '10px', fontWeight: '500' }}>{game.name}</p>
            <img
              src={game.src}
              alt={game.alt}
              style={{
                width: '150px',
                height: '100px',
                objectFit: 'cover',
                borderRadius: '8px',
                boxShadow: '0 4px 12px rgba(0,0,0,0.4)',
                cursor: 'pointer',
                transition: 'transform 0.2s',
              }}
              onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
              onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')}
            />
          </div>
        ))}
      </div>

      {/* Animatie pentru neon text */}
      <style>{`
        @keyframes gradientText {
          0% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
          100% {
            background-position: 0% 50%;
          }
        }
      `}</style>
    </div>
  );
};

export default Home;
