import React, { useEffect, useState } from 'react';
import { Pariu } from '../types/Pariu';

const Pariuri: React.FC = () => {
  const [pariuri, setPariuri] = useState<Pariu[]>([]);

  useEffect(() => {
    const mockPariuri: Pariu[] = [
      {
        IDPariu: 1,
        IDUtilizator: 101,
        IDJoc: 5,
        SumaPariata: 100.0,
        SumaCastigata: 250.0,
        DataPariu: '2025-06-04T12:00:00',
      },
      {
        IDPariu: 2,
        IDUtilizator: 102,
        IDJoc: 2,
        SumaPariata: 50.0,
        SumaCastigata: 0.0,
        DataPariu: '2025-06-03T15:45:00',
      },
    ];

    setPariuri(mockPariuri);
  }, []);

  return (
    <div
      className="min-h-screen w-full bg-cover bg-center flex items-center justify-center p-6"
      style={{ backgroundImage: "url('/images/background-casino.jpg')" }}
    >
      {/* Containerul cu fundal negru semi-transparent și conținut */}
      <div className="max-w-4xl w-full bg-black bg-opacity-70 rounded-md shadow-md p-6 text-white">
        <h1 className="text-2xl font-bold mb-4 text-center">Pariurile Tale</h1>
        <table className="w-full table-auto border border-gray-600">
          <thead className="bg-gray-800">
            <tr>
              <th className="p-2 border">#</th>
              <th className="p-2 border">ID Utilizator</th>
              <th className="p-2 border">ID Joc</th>
              <th className="p-2 border">Suma Pariată</th>
              <th className="p-2 border">Suma Câștigată</th>
              <th className="p-2 border">Data</th>
            </tr>
          </thead>
          <tbody>
            {pariuri.map((pariu) => (
              <tr key={pariu.IDPariu} className="text-center border-t border-gray-600">
                <td className="p-2 border">{pariu.IDPariu}</td>
                <td className="p-2 border">{pariu.IDUtilizator}</td>
                <td className="p-2 border">{pariu.IDJoc}</td>
                <td className="p-2 border">{pariu.SumaPariata.toFixed(2)} RON</td>
                <td className="p-2 border">{pariu.SumaCastigata.toFixed(2)} RON</td>
                <td className="p-2 border">
                  {new Date(pariu.DataPariu).toLocaleString('ro-RO')}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Pariuri;
