import { useState } from "react";

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    prenume: "",
    nume: "",
    email: "",
    parola: "",
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch("https://localhost:7239/api/Client", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          EmailClient: formData.email,
          ParolaClient: formData.parola,
          NumeClient: formData.nume,
          PrenumeClient: formData.prenume,
        }),
      });

      if (!response.ok) {
        throw new Error("Eroare la înregistrare!");
      }

      alert("Înregistrare reușită!");
    } catch (err) {
      console.error(err);
      alert("Eroare la înregistrare!");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4 bg-white">
      <h2 className="text-3xl font-bold mb-6">Înregistrare Client</h2>
      <form onSubmit={handleSubmit} className="w-full max-w-md space-y-4">
        <div>
          <input
            name="prenume"
            placeholder="Prenume"
            className="w-full p-3 border rounded"
            onChange={handleChange}
            required
          />
          <input
            name="nume"
            placeholder="Nume"
            className="w-full p-3 border rounded"
            onChange={handleChange}
            required
          />
          <input
            name="email"
            type="email"
            placeholder="Email"
            className="w-full p-3 border rounded"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <input
            name="parola"
            type="password"
            placeholder="Parolă"
            className="w-full p-3 border rounded"
            onChange={handleChange}
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
        >
          Înregistrează-te
        </button>
      </form>
    </div>
  );
};

export default RegisterPage;
