import React from "react";
import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav
      style={{
        padding: "1rem",
        background:
          "linear-gradient(90deg, #87CEEB 0%, #00BFFF 50%, #1E90FF 100%)", // un gradient albastru plăcut
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
      }}
    >
      <h1
        style={{
          marginBottom: "0.75rem",
          color: "#fff",
          textAlign: "center",
          fontWeight: "bold",
          fontSize: "1.8rem",
          userSelect: "none",
        }}
      >
        ✈️ AirSwift
      </h1>
      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "0.5rem",
          justifyContent: "center",
        }}
      >
        {pages.map(({ name, path }) => {
          const isActive = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              style={{
                padding: "0.5rem 1rem",
                borderRadius: "5px",
                fontWeight: "600",
                fontSize: "0.9rem",
                cursor: "pointer",
                border: "none",
                backgroundColor: isActive ? "#004080" : "#e0f0ff",
                color: isActive ? "#fff" : "#004080",
                transition: "background-color 0.3s, color 0.3s",
                minWidth: "100px",
                userSelect: "none",
              }}
            >
              {name}
            </button>
          );
        })}
      </div>
    </nav>
  );
};

export default Navbar;
