import React, { useState, useEffect } from 'react';

const symbols = [
  'üçí', // cire»ôe
  'üçä', // portocale
  'üçá', // struguri
  'üçâ', // pepene
  'üîî', // clopo»õel
  '7Ô∏è‚É£', // septar
  '‚≠ê', // stea (scatter)
  'üëë', // coroana (wild)
];

const symbolTypes: Record<string, 'wild' | 'scatter' | 'normal' | 'high'> = {
  'üçí': 'normal',
  'üçä': 'normal',
  'üçá': 'normal',
  'üçâ': 'normal',
  'üîî': 'normal',
  '7Ô∏è‚É£': 'high',
  '‚≠ê': 'scatter',
  'üëë': 'wild',
};

const getRandomSymbol = () => symbols[Math.floor(Math.random() * symbols.length)];

const generateReels = () => {
  return Array.from({ length: 5 }, () =>
    Array.from({ length: 3 }, () => getRandomSymbol())
  );
};

const SlotMachine: React.FC = () => {
  const [reels, setReels] = useState<string[][]>(generateReels());
  const [spinning, setSpinning] = useState(false);
  const [message, setMessage] = useState('');

  const spin = () => {
    if (spinning) return;
    setSpinning(true);
    setMessage('Se √Ænv√¢rte...');
    let spinCount = 0;
    const interval = setInterval(() => {
      setReels(generateReels());
      spinCount++;
      if (spinCount > 20) {
        clearInterval(interval);
        setSpinning(false);
        checkWin();
      }
    }, 100);
  };

  const checkWin = () => {
    let winMessage = '';
    for (let row = 0; row < 3; row++) {
      const line = reels.map(col => col[row]);
      if (line.every((sym) => sym === line[0])) {
        if (symbolTypes[line[0]] === 'scatter') {
          winMessage += '‚≠ê Scatter activat!\n';
        } else if (symbolTypes[line[0]] === 'wild') {
winMessage += 'üëë CoroanƒÉ Wild extinsƒÉ! Ai c√¢»ôtigat!\n';
        } else {
          winMessage += `üéâ Linie c√¢»ôtigƒÉtoare cu ${line[0]}!\n`;
        }
      }
    }
    if (!winMessage) winMessage = '‚ùå Nicio linie completƒÉ. √éncearcƒÉ din nou!';
    setMessage(winMessage);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-black via-purple-900 to-black text-yellow-400 p-4">
      <h1 className="text-4xl font-bold mb-4 drop-shadow-lg">üé∞ Shiny Crown ‚Äì Slot Machine üëë</h1>

      <div className="grid grid-cols-5 gap-2 bg-black p-4 rounded-lg shadow-xl">
        {reels.map((col, colIdx) =>
          col.map((symbol, rowIdx) => (
            <div
              key={`${colIdx}-${rowIdx}`}
              className={`w-16 h-16 flex items-center justify-center text-2xl rounded border-2 border-yellow-400 shadow-inner bg-gray-900 animate-fade`}
            >
              {symbol}
            </div>
          ))
        )}
      </div>

      <button
        onClick={spin}
        disabled={spinning}
        className="mt-6 px-6 py-2 text-lg font-bold bg-yellow-400 text-black rounded hover:bg-yellow-300 transition"
      >
        {spinning ? 'Se √Ænv√¢rte...' : 'üé∞ SPIN'}
      </button>

      <div className="mt-4 whitespace-pre text-center font-mono text-white max-w-md">
        {message}
      </div>
    </div>
  );
};

export default SlotMachine;
