import React from 'react';

const Home: React.FC = () => {
  const games = [
    { src: '/images/blackjack.jpg', alt: 'Blackjack', name: 'Blackjack' },
    { src: '/images/poker-table.jpg', alt: 'Poker', name: 'Poker' },
    { src: '/images/roulette-wheel.jpg', alt: 'Ruletă', name: 'Ruletă' },
    { src: '/images/slot-machine.jpg', alt: 'Slot Machine', name: 'Slot Machine' },
  ];

  return (
    <div
      className="text-white flex flex-col items-center justify-center px-6"
      style={{
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        height: '100vh', // full height of the viewport
      }}
    >
      <header className="mb-10 flex items-center space-x-4">
        <img
          src="/images/casino-logo192.png"
          alt="Casino Logo"
          style={{ width: '80px', height: '80px', borderRadius: '8px' }}
        />
        <h1 className="text-5xl font-bold drop-shadow-lg max-w-xl">
          Bun venit la Casino Online
        </h1>
      </header>

      <p className="text-lg max-w-xl text-center drop-shadow-md mb-12 px-4">
        Joacă cele mai tari jocuri de cazino: Blackjack, Poker, Ruletă, Slot Machines și multe altele!
      </p>

      {/* Grid layout pentru imagini */}
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, auto)',
          gap: '40px',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {games.map((game) => (
          <div key={game.name} style={{ textAlign: 'center' }}>
            <p className="text-md font-medium mb-2 drop-shadow-md">{game.name}</p>
            <img
              src={game.src}
              alt={game.alt}
              style={{
                width: '180px',
                height: '120px',
                objectFit: 'cover',
                borderRadius: '6px',
                boxShadow: '0 4px 8px rgba(0,0,0,0.3)',
                cursor: 'pointer',
                transition: 'transform 0.2s',
              }}
              onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
              onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
