// src/components/Navbar.tsx
import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Cauta", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav className="bg-blue-700 py-4 shadow-md">
      <div className="container mx-auto flex flex-wrap justify-between items-center px-4">
        <h1 className="text-2xl font-bold text-white">✈️ AirSwift</h1>
        <div className="flex flex-wrap gap-3">
          {pages.map(({ name, path }) => (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={`px-4 py-2 rounded-md font-medium transition
                ${
                  location.pathname === path
                    ? "bg-white text-blue-700"
                    : "bg-blue-700 text-white hover:bg-white hover:text-blue-700"
                }`}
            >
              {name}
            </button>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
