import React, { useState, useEffect } from 'react';

interface Card {
  suit: string;
  value: string;
  weight: number;
}

const generateDeck = (): Card[] => {
  const suits = ['‚ô†', '‚ô•', '‚ô¶', '‚ô£'];
  const values = [
    { value: 'A', weight: 11 },
    { value: '2', weight: 2 },
    { value: '3', weight: 3 },
    { value: '4', weight: 4 },
    { value: '5', weight: 5 },
    { value: '6', weight: 6 },
    { value: '7', weight: 7 },
    { value: '8', weight: 8 },
    { value: '9', weight: 9 },
    { value: '10', weight: 10 },
    { value: 'J', weight: 10 },
    { value: 'Q', weight: 10 },
    { value: 'K', weight: 10 },
  ];

  let deck: Card[] = [];
  suits.forEach((suit) => {
    values.forEach(({ value, weight }) => {
      deck.push({ suit, value, weight });
    });
  });

  return deck.sort(() => Math.random() - 0.5);
};

const calculateScore = (hand: Card[]): number => {
  let total = 0;
  let aces = 0;

  hand.forEach((card) => {
    total += card.weight;
    if (card.value === 'A') aces++;
  });

  while (total > 21 && aces > 0) {
    total -= 10;
    aces--;
  }

  return total;
};

const Blackjack: React.FC = () => {
  const [deck, setDeck] = useState<Card[]>([]);
  const [playerHand, setPlayerHand] = useState<Card[]>([]);
  const [dealerHand, setDealerHand] = useState<Card[]>([]);
  const [gameOver, setGameOver] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    startNewGame();
  }, []);

  const drawCard = (currentDeck: Card[]): [Card, Card[]] => {
    const card = currentDeck[0];
    const remaining = currentDeck.slice(1);
    return [card, remaining];
  };

  const startNewGame = () => {
    const newDeck = generateDeck();
    let d = [...newDeck];

    const [playerCard1, d1] = drawCard(d);
    const [dealerCard1, d2] = drawCard(d1);
    const [playerCard2, d3] = drawCard(d2);
    const [dealerCard2, d4] = drawCard(d3);

    setPlayerHand([playerCard1, playerCard2]);
    setDealerHand([dealerCard1, dealerCard2]);
    setDeck(d4);
    setMessage('');
    setGameOver(false);
  };

  const handleHit = () => {
    const [newCard, newDeck] = drawCard(deck);
    const newHand = [...playerHand, newCard];
    setPlayerHand(newHand);
    setDeck(newDeck);

    const score = calculateScore(newHand);
    if (score > 21) {
      setMessage('Ai pierdut! üò¢');
      setGameOver(true);
    }
  };

  const handleStand = () => {
    let dHand = [...dealerHand];
    let d = [...deck];

    while (calculateScore(dHand) < 17) {
      const [newCard, newDeck] = drawCard(d);
      dHand.push(newCard);
      d = newDeck;
    }

    const playerScore = calculateScore(playerHand);
    const dealerScore = calculateScore(dHand);

    let result = '';
    if (dealerScore > 21 || playerScore > dealerScore) {
      result = 'Ai c√¢»ôtigat! üéâ';
    } else if (playerScore < dealerScore) {
      result = 'Dealerul a c√¢»ôtigat! üòê';
    } else {
      result = 'Egalitate! ü§ù';
    }

    setDealerHand(dHand);
    setMessage(result);
    setGameOver(true);
  };

  const renderHand = (hand: Card[]) => (
    <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
      {hand.map((card, i) => (
        <div
          key={i}
          style={{
            border: '1px solid #ccc',
            padding: '8px 14px',
            borderRadius: 10,
            backgroundColor: 'white',
            color: card.suit === '‚ô•' || card.suit === '‚ô¶' ? 'red' : 'black',
            boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
            minWidth: 52,
            textAlign: 'center',
            userSelect: 'none',
            fontWeight: '700',
            fontSize: 20,
          }}
        >
          <div>{card.value}</div>
          <div>{card.suit}</div>
        </div>
      ))}
    </div>
  );

  return (
    <div
      style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #1e3c72, #2a5298)', // fundal elegant albastru
        color: '#fff',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '2rem',
        fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif',
      }}
    >
      <h1 style={{ fontSize: 36, marginBottom: 30, fontWeight: '900' }}>
        ‚ô†Ô∏è Blackjack
      </h1>

      <div style={{ marginBottom: 28, width: '100%', maxWidth: 600 }}>
        <h2 style={{ marginBottom: 10 }}>
          üßë JucƒÉtor - {calculateScore(playerHand)} puncte
        </h2>
        {renderHand(playerHand)}
      </div>

      <div style={{ marginBottom: 28, width: '100%', maxWidth: 600 }}>
        <h2 style={{ marginBottom: 10 }}>
          üé≤ Dealer - {gameOver ? calculateScore(dealerHand) : '??'} puncte
        </h2>
        {gameOver ? renderHand(dealerHand) : <p>CƒÉr»õi ascunse...</p>}
      </div>

      <div style={{ marginBottom: 28 }}>
        {!gameOver && (
          <>
            <button onClick={handleHit} style={buttonStyle}>
              Hit üÉè
            </button>
            <button
              onClick={handleStand}
              style={{ ...buttonStyle, backgroundColor: '#f59e0b' }}
            >
              Stand ‚úã
            </button>
          </>
        )}
        {gameOver && (
          <button
            onClick={startNewGame}
            style={{ ...buttonStyle, backgroundColor: '#3b82f6' }}
          >
            JoacƒÉ din nou üîÅ
          </button>
        )}
      </div>

      {message && (
        <div
          style={{
            marginTop: 18,
            fontSize: 20,
            fontWeight: '700',
            color: message.includes('c√¢»ôtigat') ? '#4ade80' : '#f87171',
            textShadow: '0 0 4px rgba(0,0,0,0.3)',
          }}
        >
          {message}
        </div>
      )}
    </div>
  );
};

const buttonStyle: React.CSSProperties = {
  padding: '12px 24px',
  margin: '0 12px',
  border: 'none',
  borderRadius: 10,
  backgroundColor: '#16a34a',
  color: 'white',
  fontSize: 17,
  fontWeight: '700',
  cursor: 'pointer',
  userSelect: 'none',
  transition: 'background-color 0.3s ease',
};

export default Blackjack;
