import React, { useState } from 'react';

const SYMBOLS = ['ðŸ’', 'ðŸ‹', 'ðŸ‡', 'ðŸ””', 'ðŸ‰', 'â­', 'ðŸ‘‘', '7ï¸âƒ£'];
const REELS = 5; // coloane
const ROWS = 3;  // linii

type ColorChoice = 'red' | 'black';

const StoneSlot: React.FC = () => {
  // Matrice 5x3 simboluri
  const [reels, setReels] = useState<string[][]>(
    Array.from({ length: ROWS }, () =>
      Array.from({ length: REELS }, () => getRandomSymbol())
    )
  );

  const [spinning, setSpinning] = useState(false);
  const [canCollect, setCanCollect] = useState(false);
  const [winAmount, setWinAmount] = useState(0);
  const [doublingActive, setDoublingActive] = useState(false);
  const [doubleCount, setDoubleCount] = useState(0);
  const [message, setMessage] = useState<string | null>(null);

  function getRandomSymbol() {
    return SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)];
  }

  // Simulare cÃ¢È™tig: dacÄƒ Ã®n linia de mijloc sunt 3 sau mai multe 7ï¸âƒ£ consecutive
  const checkWin = (currentReels: string[][]): number => {
    const middleRow = currentReels[1]; // linia 2 (index 1)
    let maxSeq = 0;
    let currentSeq = 0;

    for (let i = 0; i < REELS; i++) {
      if (middleRow[i] === '7ï¸âƒ£') {
        currentSeq++;
        if (currentSeq > maxSeq) maxSeq = currentSeq;
      } else {
        currentSeq = 0;
      }
    }

    if (maxSeq >= 3) {
      return maxSeq * 10; // cÃ¢È™tig 10 monede pe simbol 7ï¸âƒ£ consecutiv
    }
    return 0;
  };

  // Animatie rotire cu interval pe fiecare coloanÄƒ
  const spin = () => {
    if (spinning) return;

    setSpinning(true);
    setCanCollect(false);
    setDoublingActive(false);
    setMessage(null);
    setDoubleCount(0);
    setWinAmount(0);

    let spinsLeft = REELS; // cÃ¢te coloane mai trebuie sa se opreasca

    // Copiem array-ul pentru update incremental
    let currentReels = Array.from({ length: ROWS }, () =>
      Array.from({ length: REELS }, () => getRandomSymbol())
    );

    setReels(currentReels);

    const intervals: NodeJS.Timeout[] = [];

    for (let col = 0; col < REELS; col++) {
      let spinCount = 0;
      const maxSpinCount = 15 + col * 5; // fiecare col stopeaza mai tÃ¢rziu

      intervals[col] = setInterval(() => {
        if (spinCount >= maxSpinCount) {
          clearInterval(intervals[col]);
          spinsLeft--;

          // CÃ¢nd toate coloanele s-au oprit
          if (spinsLeft === 0) {
            setSpinning(false);
            const win = checkWin(currentReels);
            if (win > 0) {
              setWinAmount(win);
              setMessage(`Ai cÃ¢È™tigat ${win} monede! ðŸŽ‰`);
              setCanCollect(true);
            } else {
              setMessage('Nu ai cÃ¢È™tigat, Ã®ncearcÄƒ din nou!');
            }
          }
          return;
        }

        // ActualizeazÄƒ simbolurile doar pe coloana curentÄƒ
        for (let row = 0; row < ROWS; row++) {
          currentReels[row][col] = getRandomSymbol();
        }
        setReels(currentReels.map(row => [...row])); // clonÄƒm pt React sÄƒ È™tie cÄƒ e update

        spinCount++;
      }, 75);
    }
  };

  const collect = () => {
    setCanCollect(false);
    setMessage(null);
    setWinAmount(0);
  };

  const doubleGuess = (choice: ColorChoice) => {
    const colors: ColorChoice[] = ['red', 'black'];
    const actual = colors[Math.floor(Math.random() * colors.length)];

    if (choice === actual) {
      const newCount = doubleCount + 1;
      const newWin = winAmount * 2;
      setDoubleCount(newCount);
      setWinAmount(newWin);
      setMessage(`Corect! CÃ¢È™tig dublat x${2 ** newCount}!`);

      if (newCount >= 5) {
        setMessage(`FelicitÄƒri! Ai dublat maxim x${2 ** newCount}.`);
        setDoublingActive(false);
        setCanCollect(true);
      }
    } else {
      setMessage('Mai Ã®ncearcÄƒ! Ai pierdut dublarea.');
      setDoublingActive(false);
      setCanCollect(true);
    }
  };

  const startDoubling = () => {
    if (winAmount > 0) {
      setDoublingActive(true);
      setCanCollect(false);
      setMessage('Alege roÈ™u sau negru pentru dublare!');
      setDoubleCount(0);
    }
  };

  return (
    <div
      style={{
        textAlign: 'center',
        marginTop: 50,
        color: '#fff',
        fontFamily: 'Arial',
        backgroundColor: '#111',
        minHeight: '100vh',
        padding: 20,
      }}
    >
      <h1>StoneSlot ðŸŽ°</h1>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: `repeat(${REELS}, 70px)`,
          gridTemplateRows: `repeat(${ROWS}, 70px)`,
          gap: 12,
          justifyContent: 'center',
          marginBottom: 30,
          backgroundColor: '#222',
          padding: 20,
          borderRadius: 12,
          boxShadow: '0 0 15px gold',
          width: 'fit-content',
          marginLeft: 'auto',
          marginRight: 'auto',
        }}
      >
        {reels.map((row, rowIndex) =>
          row.map((symbol, colIndex) => (
            <div
              key={`${rowIndex}-${colIndex}`}
              style={{
                width: 70,
                height: 70,
                fontSize: 38,
                backgroundColor: '#000',
                color: 'gold',
                borderRadius: 8,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                boxShadow: 'inset 0 0 10px #facc15',
                userSelect: 'none',
              }}
            >
              {symbol}
            </div>
          ))
        )}
      </div>

      {/* Mesaj status */}
      {message && (
        <div
          style={{
            marginBottom: 20,
            fontWeight: 'bold',
            fontSize: 18,
            color: doublingActive ? 'yellow' : 'lightgreen',
          }}
        >
          {message}
        </div>
      )}

      {/* Buton rulare normalÄƒ */}
      {!spinning && !canCollect && !doublingActive && (
        <button
          onClick={spin}
          style={{
            padding: '12px 28px',
            fontSize: 18,
            fontWeight: 'bold',
            backgroundColor: '#10b981',
            border: 'none',
            borderRadius: 8,
            color: 'white',
            cursor: 'pointer',
            boxShadow: '0 0 10px #10b981',
            transition: 'background-color 0.3s',
          }}
        >
          RuleazÄƒ roata! ðŸŽ°
        </button>
      )}

      {/* Buton Ã®ncaseazÄƒ dupÄƒ cÃ¢È™tig */}
      {canCollect && !doublingActive && (
        <>
          <button
            onClick={collect}
            style={{
              padding: '10px 26px',
              fontSize: 16,
              fontWeight: 'bold',
              backgroundColor: '#f59e0b',
              border: 'none',
              borderRadius: 8,
              color: 'black',
              cursor: 'pointer',
              boxShadow: '0 0 8px #f59e0b',
              marginRight: 12,
            }}
          >
            ÃŽncaseazÄƒ
          </button>

          <button
            onClick={startDoubling}
            style={{
              padding: '10px 26px',
              fontSize: 16,
              fontWeight: 'bold',
              backgroundColor: '#ef4444',
              border: 'none',
              borderRadius: 8,
              color: 'white',
              cursor: 'pointer',
              boxShadow: '0 0 8px #ef4444',
            }}
          >
            DubleazÄƒ
          </button>
        </>
      )}

      {/* Buton dublare */}
      {doublingActive && (
        <div style={{ marginTop: 20 }}>
          <button
            onClick={() => doubleGuess('red')}
            style={{
              padding: '12px 24px',
              marginRight: 15,
              fontSize: 18,
              fontWeight: 'bold',
              backgroundColor: '#dc2626',
              color: 'white',
              borderRadius: 8,
              border: 'none',
              cursor: 'pointer',
              boxShadow: '0 0 10px #dc2626',
            }}
          >
            RoÈ™u
          </button>
          <button
            onClick={() => doubleGuess('black')}
            style={{
              padding: '12px 24px',
              fontSize: 18,
              fontWeight: 'bold',
              backgroundColor: '#1f2937',
              color: 'white',
              borderRadius: 8,
              border: 'none',
              cursor: 'pointer',
              boxShadow: '0 0 10px #374151',
            }}
          >
            Negru
          </button>
        </div>
      )}
    </div>
  );
};

export default StoneSlot;
