import { useState } from "react";

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const [errors, setErrors] = useState<{ [key: string]: string }>({});
  const [successMessage, setSuccessMessage] = useState("");

  const validate = () => {
    const newErrors: { [key: string]: string } = {};
    if (!formData.name.trim()) newErrors.name = "Numele este obligatoriu.";
    if (!formData.email.trim()) {
      newErrors.email = "Email-ul este obligatoriu.";
    } else if (
      !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(formData.email)
    ) {
      newErrors.email = "Email-ul nu este valid.";
    }
    if (!formData.subject.trim()) newErrors.subject = "Subiectul este obligatoriu.";
    if (!formData.message.trim()) newErrors.message = "Mesajul este obligatoriu.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (errors[e.target.name]) {
      setErrors({ ...errors, [e.target.name]: "" }); // șterge eroarea când user modifică
    }
    setSuccessMessage(""); // ascunde mesajul de succes dacă modifică ceva
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!validate()) return;

    // aici poți adăuga trimiterea la API

    console.log("Form data trimis:", formData);
    setSuccessMessage("Mesaj trimis cu succes!");
    setFormData({ name: "", email: "", subject: "", message: "" });
    setErrors({});
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 px-4 py-8">
      <h2 className="text-3xl font-bold mb-6">Contactează-ne</h2>

      <form
        onSubmit={handleSubmit}
        className="w-full max-w-md space-y-4 bg-white p-6 rounded-xl shadow"
        noValidate
      >
        <div>
          <input
            type="text"
            name="name"
            placeholder="Nume"
            value={formData.name}
            onChange={handleChange}
            aria-invalid={!!errors.name}
            aria-describedby={errors.name ? "name-error" : undefined}
            className={`w-full p-3 border rounded ${
              errors.name ? "border-red-600" : "border-gray-300"
            }`}
          />
          {errors.name && (
            <p
              id="name-error"
              className="mt-1 text-sm text-red-600 flex items-center gap-1"
            >
              <span>⚠️</span> {errors.name}
            </p>
          )}
        </div>

        <div>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            aria-invalid={!!errors.email}
            aria-describedby={errors.email ? "email-error" : undefined}
            className={`w-full p-3 border rounded ${
              errors.email ? "border-red-600" : "border-gray-300"
            }`}
          />
          {errors.email && (
            <p
              id="email-error"
              className="mt-1 text-sm text-red-600 flex items-center gap-1"
            >
              <span>⚠️</span> {errors.email}
            </p>
          )}
        </div>

        <div>
          <input
            type="text"
            name="subject"
            placeholder="Subiect"
            value={formData.subject}
            onChange={handleChange}
            aria-invalid={!!errors.subject}
            aria-describedby={errors.subject ? "subject-error" : undefined}
            className={`w-full p-3 border rounded ${
              errors.subject ? "border-red-600" : "border-gray-300"
            }`}
          />
          {errors.subject && (
            <p
              id="subject-error"
              className="mt-1 text-sm text-red-600 flex items-center gap-1"
            >
              <span>⚠️</span> {errors.subject}
            </p>
          )}
        </div>

        <div>
          <textarea
            name="message"
            placeholder="Mesaj"
            value={formData.message}
            onChange={handleChange}
            aria-invalid={!!errors.message}
            aria-describedby={errors.message ? "message-error" : undefined}
            className={`w-full p-3 border rounded ${
              errors.message ? "border-red-600" : "border-gray-300"
            }`}
          />
          {errors.message && (
            <p
              id="message-error"
              className="mt-1 text-sm text-red-600 flex items-center gap-1"
            >
              <span>⚠️</span> {errors.message}
            </p>
          )}
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Trimite Mesajul
        </button>
      </form>

      {successMessage && (
        <p className="mt-4 text-green-600 font-semibold">{successMessage}</p>
      )}
    </div>
  );
};

export default ContactPage;
