import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navbar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: 'Home', path: '/' },
    { name: 'Pariuri', path: '/pariuri' },
    { name: 'Jocuri', path: '/jocuri' },
    { name: 'Tranzactii', path: '/tranzactii' },
    { name: 'Deposit', path: '/deposit' },
    { name: 'Profil', path: '/profil' },
    { name: 'Login', path: '/login' },
    { name: 'Register', path: '/register' },
  ];

  return (
    <nav
      style={{
        padding: '1rem',
        background: `linear-gradient(
          135deg,
          rgba(10, 10, 30, 0.95),
          rgba(20, 20, 60, 0.95),
          rgba(30, 30, 80, 0.95)
        )`,
        backgroundBlendMode: 'overlay',
        backgroundImage:
          'repeating-linear-gradient(45deg, rgba(255,255,255,0.02) 0px, rgba(255,255,255,0.02) 1px, transparent 1px, transparent 4px)',
        boxShadow: '0 2px 12px rgba(0,0,0,0.4)',
        color: '#f0f0f0',
        userSelect: 'none',
        borderBottom: '2px solid #ffcc00',
      }}
    >
      <h1
        style={{
          marginBottom: '0.75rem',
          color: '#ffcc00',
          textAlign: 'center',
          fontWeight: 'bold',
          fontSize: '2rem',
          cursor: 'default',
          userSelect: 'none',
          textShadow: '1px 1px 2px black',
        }}
      >
        ðŸŽ° CasinoApp
      </h1>

      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '0.75rem',
          justifyContent: 'center',
        }}
      >
        {pages.map(({ name, path }) => {
          const isActive = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              title={`NavigheazÄƒ la ${name}`}
              style={{
                padding: '0.6rem 1.2rem',
                borderRadius: '6px',
                fontWeight: 600,
                fontSize: '0.95rem',
                cursor: 'pointer',
                border: '1px solid #ffcc00',
                backgroundColor: isActive ? '#ffcc00' : 'transparent',
                color: isActive ? '#1a1a1a' : '#f0f0f0',
                transition: 'all 0.3s ease',
              }}
            >
              {name}
            </button>
          );
        })}
      </div>

      <footer
        style={{
          marginTop: '1.5rem',
          textAlign: 'center',
          fontSize: '0.8rem',
          color: '#cccccc',
          paddingTop: '0.75rem',
          borderTop: '1px solid #444',
        }}
      >
        Â© 2025 CasinoApp
      </footer>
    </nav>
  );
};

export default Navbar;
