import { useState } from "react";

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log(formData);
    alert("Mesaj trimis cu succes!");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 px-6 py-12">
      <h2 className="text-4xl font-extrabold text-indigo-700 mb-10 relative after:absolute after:bottom-0 after:left-0 after:w-24 after:h-1 after:bg-indigo-400 after:rounded-sm after:mt-2">
        Contactează-ne
      </h2>
      <form
        onSubmit={handleSubmit}
        className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-lg space-y-6"
        noValidate
      >
        <div className="grid grid-cols-1 gap-5 md:grid-cols-2">
          <input
            type="text"
            name="name"
            placeholder="Nume"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-4 border border-gray-300 rounded-xl placeholder-gray-400 focus:outline-none focus:ring-3 focus:ring-indigo-300 transition"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-4 border border-gray-300 rounded-xl placeholder-gray-400 focus:outline-none focus:ring-3 focus:ring-indigo-300 transition"
            required
          />
          <input
            type="text"
            name="subject"
            placeholder="Subiect"
            value={formData.subject}
            onChange={handleChange}
            className="w-full p-4 border border-gray-300 rounded-xl placeholder-gray-400 focus:outline-none focus:ring-3 focus:ring-indigo-300 transition md:col-span-2"
            required
          />
        </div>
        <textarea
          name="message"
          placeholder="Mesaj"
          value={formData.message}
          onChange={handleChange}
          className="w-full p-4 border border-gray-300 rounded-xl placeholder-gray-400 focus:outline-none focus:ring-3 focus:ring-indigo-300 transition resize-none h-32"
          required
        />

        {/* Butonul mutat jos și centrat */}
        <div className="flex justify-center pt-4">
          <button
            type="submit"
            className="bg-indigo-600 text-white py-3 px-10 rounded-xl shadow-md hover:bg-indigo-700 hover:shadow-lg transition duration-300"
          >
            Trimite Mesajul
          </button>
        </div>
      </form>
    </div>
  );
};

export default ContactPage;
