import { useState } from "react";
import { FaSignOutAlt } from "react-icons/fa";

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log(formData);
    alert("Mesaj trimis cu succes!");
    setFormData({ name: "", email: "", subject: "", message: "" }); // resetare form
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-tr from-indigo-100 via-purple-100 to-pink-100 px-6 py-12">
      <h2 className="text-4xl font-extrabold mb-8 text-gray-800 drop-shadow-md">
        ContacteazÄƒ-ne
      </h2>
      <form
        onSubmit={handleSubmit}
        className="w-full max-w-lg bg-white rounded-2xl p-8 shadow-lg ring-1 ring-indigo-200"
      >
        <div className="space-y-6">
          <input
            type="text"
            name="name"
            placeholder="Nume"
            value={formData.name}
            onChange={handleChange}
            required
            className="w-full px-4 py-3 rounded-lg border border-gray-300 placeholder-gray-400
                       focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500
                       transition duration-200 ease-in-out text-gray-700 font-medium"
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full px-4 py-3 rounded-lg border border-gray-300 placeholder-gray-400
                       focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500
                       transition duration-200 ease-in-out text-gray-700 font-medium"
          />
          <input
            type="text"
            name="subject"
            placeholder="Subiect"
            value={formData.subject}
            onChange={handleChange}
            required
            className="w-full px-4 py-3 rounded-lg border border-gray-300 placeholder-gray-400
                       focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500
                       transition duration-200 ease-in-out text-gray-700 font-medium"
          />
          <textarea
            name="message"
            placeholder="Mesaj"
            value={formData.message}
            onChange={handleChange}
            required
            rows={5}
            className="w-full px-4 py-3 rounded-lg border border-gray-300 placeholder-gray-400 resize-y
                       focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500
                       transition duration-200 ease-in-out text-gray-700 font-medium"
          />
        </div>

        <button
          type="submit"
          className="mt-8 w-full bg-indigo-600 hover:bg-indigo-700 active:bg-indigo-800
                     text-white font-semibold py-3 rounded-lg shadow-md
                     transition duration-200 ease-in-out focus:outline-none focus:ring-4
                     focus:ring-indigo-300 flex justify-center items-center space-x-2"
        >
          <FaSignOutAlt size={20} />
          <span>Trimite Mesajul</span>
        </button>
      </form>
    </div>
  );
};

export default ContactPage;
