import { useEffect, useState } from "react"; 
import axios from "axios";
import { useNavigate } from "react-router-dom";

interface Client {
  idClient: number;
  emailClient: string;
  numeClient: string;
  prenumeClient: string;
}

interface Rezervare {
  id: number;
  ruta: string;
  dataRezervare: string;
  // alte câmpuri relevante din rezervare, ex: ora, numar locuri etc
}

const ProfilePage = () => {
  const [client, setClient] = useState<Client | null>(null);
  const [rezervari, setRezervari] = useState<Rezervare[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const clientId = localStorage.getItem("clientId");
    if (!clientId) {
      return;
    }

    // Preluare client
    axios.get(`https://localhost:7239/api/Client/${clientId}`)
      .then(response => {
        setClient(response.data);
      })
      .catch(error => {
        console.error("Eroare la preluarea datelor client:", error);
      });

    // Preluare rezervari client
    axios.get(`https://localhost:7239/api/Rezervari/client/${clientId}`)
      .then(response => {
        setRezervari(response.data);
      })
      .catch(error => {
        console.error("Eroare la preluarea rezervărilor:", error);
      });
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("clientId");
    navigate("/login");
  };

  return (
    <div className="max-w-md mx-auto p-5 bg-white rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-4">Profilul Meu</h2>
      {client ? (
        <div>
          <p><strong>Nume:</strong> {client.numeClient}</p>
          <p><strong>Prenume:</strong> {client.prenumeClient}</p>
          <p><strong>Email:</strong> {client.emailClient}</p>

          <h3 className="mt-6 text-xl font-semibold">Rezervările mele</h3>
          {rezervari.length > 0 ? (
            <ul className="list-disc list-inside">
              {rezervari.map((rezervare) => (
                <li key={rezervare.id}>
                  {rezervare.ruta} - Data rezervării: {new Date(rezervare.dataRezervare).toLocaleDateString()}
                </li>
              ))}
            </ul>
          ) : (
            <p>Nu ai rezervări făcute.</p>
          )}

          <button
            onClick={handleLogout}
            className="mt-4 px-4 py-2 bg-red-500 text-white rounded"
          >
            Logout
          </button>
        </div>
      ) : (
        <p>Nu ești autentificat.</p>
      )}
    </div>
  );
};

export default ProfilePage;
