import React, { useState } from 'react';

const Register: React.FC = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [parola, setParola] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    alert(`Cont creat pentru ${username}!`);
  };

  return (
    <div className="relative min-h-screen bg-cover bg-center" style={{ backgroundImage: "url('/images/background-casino.jpg')" }}>
      {/* Overlay negru transparent */}
      <div className="absolute inset-0 bg-black bg-opacity-70"></div>

      {/* Containerul formularului */}
      <div className="relative max-w-md mx-auto p-6 rounded-md shadow-md text-white">
        <h1 className="text-2xl font-bold mb-6 text-center">Înregistrare</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder="Username"
            className="w-full p-2 rounded bg-gray-800 bg-opacity-80 border border-gray-700 text-white"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            className="w-full p-2 rounded bg-gray-800 bg-opacity-80 border border-gray-700 text-white"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Parolă"
            className="w-full p-2 rounded bg-gray-800 bg-opacity-80 border border-gray-700 text-white"
            value={parola}
            onChange={(e) => setParola(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-green-600 py-2 rounded hover:bg-green-700"
          >
            Creează cont
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
