import { useNavigate, useLocation } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const pages = [
    { name: "Acasă", path: "/" },
    { name: "Caută", path: "/flights" },
    { name: "Înregistrare", path: "/register" },
    { name: "Autentificare", path: "/login" },
    { name: "Check-in", path: "/checkin" },
    { name: "Profil", path: "/profile" },
    { name: "Contact", path: "/contact" },
    { name: "Despre Noi", path: "/about" },
  ];

  return (
    <nav className="fixed inset-0 flex flex-col items-center justify-center z-50 bg-white/80">
      <h1 className="text-2xl font-bold text-blue-900 mb-4">✈️ AirSwift</h1>
      <div className="flex flex-wrap justify-center gap-2 px-6 py-2 rounded-md shadow-md">
        {pages.map(({ name, path }) => (
          <button
            key={path}
            onClick={() => navigate(path)}
            className={`px-3 py-1 rounded-md font-medium transition text-sm
              ${
                location.pathname === path
                  ? "bg-blue-700 text-white"
                  : "bg-white text-blue-700 hover:bg-blue-700 hover:text-white"
              }`}
          >
            {name}
          </button>
        ))}
      </div>
    </nav>
  );
};

export default Navbar;
