import React from 'react';

const Home: React.FC = () => {
  const games = [
    { src: '/images/blackjack.jpg', alt: 'Blackjack', name: 'Blackjack' },
    { src: '/images/poker-table.jpg', alt: 'Poker', name: 'Poker' },
    { src: '/images/roulette-wheel.jpg', alt: 'Ruletă', name: 'Ruletă' },
    { src: '/images/slot-machine.jpg', alt: 'Slot Machine', name: 'Slot Machine' },
  ];

  return (
    <div
      style={{
        position: 'relative',
        minHeight: '100vh',
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#f0f0f0',
        padding: '40px 20px',
        overflow: 'hidden',
      }}
    >
      {/* Overlay întunecat peste background */}
      <div
        style={{
          position: 'absolute',
          inset: 0,
          background:
            'linear-gradient(135deg, rgba(20,20,20,0.85), rgba(40,10,10,0.85))',
          zIndex: 0,
        }}
      />

      {/* Container conținut, peste overlay */}
      <div style={{ position: 'relative', zIndex: 1, maxWidth: '900px', textAlign: 'center' }}>
        <img
          src="/images/casino-logo192.png"
          alt="Casino Logo"
          style={{
            width: '100px',
            height: '100px',
            borderRadius: '15px',
            marginBottom: '30px',
            boxShadow: '0 0 20px 5px #ff4d4d',
            animation: 'glow 3s ease-in-out infinite',
          }}
        />
        <h1
          style={{
            fontSize: '4rem',
            fontWeight: '900',
            marginBottom: '20px',
            textShadow: '0 0 12px #ff4d4d, 0 0 20px #ff0000',
            letterSpacing: '3px',
            fontFamily: "'Orbitron', sans-serif",
          }}
        >
          Bun venit la Casino Online
        </h1>
        <p
          style={{
            fontSize: '1.5rem',
            maxWidth: '700px',
            margin: '0 auto 50px',
            fontWeight: '500',
            color: '#ffd6d6',
            textShadow: '0 0 5px rgba(255, 77, 77, 0.8)',
          }}
        >
          Joacă cele mai tari jocuri de cazino: Blackjack, Poker, Ruletă, Slot Machines și multe altele!
        </p>

        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            gap: '40px',
          }}
        >
          {games.map((game) => (
            <div
              key={game.name}
              style={{
                width: '180px',
                cursor: 'pointer',
                borderRadius: '15px',
                overflow: 'hidden',
                boxShadow:
                  '0 0 12px 3px rgba(255, 77, 77, 0.7), 0 0 30px 10px rgba(255, 0, 0, 0.3)',
                transition: 'transform 0.3s, box-shadow 0.3s',
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'scale(1.1)';
                e.currentTarget.style.boxShadow =
                  '0 0 20px 5px rgba(255, 77, 77, 1), 0 0 40px 15px rgba(255, 0, 0, 0.7)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow =
                  '0 0 12px 3px rgba(255, 77, 77, 0.7), 0 0 30px 10px rgba(255, 0, 0, 0.3)';
              }}
            >
              <img
                src={game.src}
                alt={game.alt}
                style={{
                  width: '100%',
                  height: '120px',
                  objectFit: 'cover',
                  display: 'block',
                }}
              />
              <p
                style={{
                  marginTop: '12px',
                  fontWeight: '700',
                  fontSize: '1.2rem',
                  color: '#ffdddd',
                  textShadow: '0 0 6px rgba(255, 77, 77, 0.8)',
                  fontFamily: "'Orbitron', sans-serif",
                }}
              >
                {game.name}
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Keyframes pentru glow animation */}
      <style>{`
        @keyframes glow {
          0%, 100% {
            box-shadow: 0 0 15px 5px #ff4d4d;
          }
          50% {
            box-shadow: 0 0 30px 10px #ff1a1a;
          }
        }
      `}</style>
    </div>
  );
};

export default Home;
