import React from 'react';

const Home: React.FC = () => {
  const games = [
    { src: '/images/blackjack.jpg', alt: 'Blackjack', name: 'Blackjack' },
    { src: '/images/poker-table.jpg', alt: 'Poker', name: 'Poker' },
    { src: '/images/roulette-wheel.jpg', alt: 'Ruletă', name: 'Ruletă' },
    { src: '/images/slot-machine.jpg', alt: 'Slot Machine', name: 'Slot Machine' },
  ];

  // Împărțim lista în două jumătăți pentru coloane separate
  const midIndex = Math.ceil(games.length / 2);
  const leftColumn = games.slice(0, midIndex);
  const rightColumn = games.slice(midIndex);

  return (
    <div
      className="text-white flex flex-col items-center justify-center min-h-screen px-6"
      style={{
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
      }}
    >
      <header className="mb-10 flex items-center space-x-4">
        <img
          src="/images/casino-logo192.png"
          alt="Casino Logo"
          style={{ width: '240px', height: '240px', borderRadius: '0.375rem' }}
        />
        <h1 className="text-5xl font-bold drop-shadow-lg max-w-xl">
          Bun venit la Casino Online
        </h1>
      </header>

      <p className="text-lg max-w-xl text-center drop-shadow-md mb-12 px-4">
        Joacă cele mai tari jocuri de cazino: Blackjack, Poker, Ruletă, Slot Machines și multe altele!
      </p>

      {/* Container flex pentru 2 coloane */}
      <div className="flex w-full max-w-5xl gap-x-16">
        {/* Coloana 1 */}
        <div className="flex flex-col space-y-10 flex-1">
          {leftColumn.map((game) => (
            <div key={game.name} className="flex flex-col items-center">
              <h3 className="mb-4 text-2xl font-semibold drop-shadow-md">{game.name}</h3>
              <img
                src={game.src}
                alt={game.alt}
                style={{ width: '240px', height: '180px' }}
                className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer"
              />
            </div>
          ))}
        </div>

        {/* Coloana 2 */}
        <div className="flex flex-col space-y-10 flex-1">
          {rightColumn.map((game) => (
            <div key={game.name} className="flex flex-col items-center">
              <h3 className="mb-4 text-2xl font-semibold drop-shadow-md">{game.name}</h3>
              <img
                src={game.src}
                alt={game.alt}
                style={{ width: '240px', height: '180px' }}
                className="rounded shadow-lg hover:scale-105 transition-transform cursor-pointer"
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
