import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setErrorMsg("");
    try {
      const response = await axios.post(
        "https://localhost:7239/api/Auth/login",
        {
          Email: email,
          Parola: password,
        }
      );
      const user = response.data;
      console.log("Autentificat:", user);

      localStorage.setItem("clientId", user.idClient);

      navigate("/profile");
    } catch (err) {
      console.error(err);
      setErrorMsg("Credențiale greșite! Te rog încearcă din nou.");
    }
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center px-6 bg-gradient-to-r from-blue-400 via-purple-500 to-pink-500"
      style={{ fontFamily: "'Poppins', sans-serif" }}
    >
      <div
        className="bg-white/30 backdrop-blur-md rounded-3xl shadow-lg max-w-md w-full p-10
          border border-white/40 hover:shadow-2xl transition-shadow duration-500"
      >
        <h2
          className="text-4xl font-extrabold text-transparent bg-clip-text
            bg-gradient-to-r from-purple-700 via-indigo-800 to-pink-700 mb-10 text-center drop-shadow-md"
        >
          Autentificare Pasager
        </h2>
        <form onSubmit={handleLogin} className="space-y-6">
          <input
            type="email"
            placeholder="Email"
            className="w-full p-4 rounded-lg border border-gray-300 bg-white bg-opacity-70
              focus:outline-none focus:ring-4 focus:ring-indigo-500 focus:border-indigo-600
              transition duration-300"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Parolă"
            className="w-full p-4 rounded-lg border border-gray-300 bg-white bg-opacity-70
              focus:outline-none focus:ring-4 focus:ring-indigo-500 focus:border-indigo-600
              transition duration-300"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {errorMsg && (
            <p className="text-red-600 text-sm font-medium">{errorMsg}</p>
          )}
          <button
            type="submit"
            className="w-full py-3 rounded-lg bg-gradient-to-r from-indigo-600 via-indigo-700 to-indigo-800
              text-white font-semibold text-lg shadow-md hover:from-indigo-700 hover:via-indigo-800 hover:to-indigo-900
              active:scale-95 transition-transform duration-150"
          >
            Autentifică-te
          </button>
        </form>
        <p className="mt-8 text-center text-gray-200">
          Nu ai cont?{" "}
          <a
            href="/register"
            className="text-indigo-300 hover:text-indigo-100 hover:underline font-semibold"
          >
            Înregistrează-te aici
          </a>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
