import { useState } from 'react';
import axios from 'axios';
const RegisterPage = () => {
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    email: '',
    phone: '',
    address: '',
    password: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
  try {
    await axios.post('https://localhost:3001/api/pasageri/register', formData);
    alert('Înregistrare reușită!');
  } catch (err) {
    console.error(err);
    alert('Eroare la înregistrare!');
  }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4 bg-white">
      <h2 className="text-3xl font-bold mb-6">Înregistrare Pasager</h2>
      <form onSubmit={handleSubmit} className="w-full max-w-md space-y-4">
        <input name="fname" placeholder="Prenume" className="w-full p-3 border rounded" onChange={handleChange} required />
        <input name="lname" placeholder="Nume" className="w-full p-3 border rounded" onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email" className="w-full p-3 border rounded" onChange={handleChange} required />
        <input name="phone" placeholder="Telefon" className="w-full p-3 border rounded" onChange={handleChange} required />
        <input name="address" placeholder="Adresă" className="w-full p-3 border rounded" onChange={handleChange} />
        <input name="password" type="password" placeholder="Parolă" className="w-full p-3 border rounded" onChange={handleChange} required />
        <button type="submit" className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700">
          Înregistrează-te
        </button>
      </form>
    </div>
  );
};

export default RegisterPage;
