import { useNavigate } from "react-router-dom";
import { Info, Users, Rocket, Star } from "lucide-react";

const AboutPage = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col items-center relative bg-gradient-to-br from-sky-100 to-indigo-200 px-6 py-16 overflow-hidden">
      {/* Background blobs */}
      <div className="absolute top-0 left-0 w-72 h-72 bg-indigo-300 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob"></div>
      <div className="absolute top-40 right-0 w-72 h-72 bg-pink-300 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob animation-delay-2000"></div>

      <h2 className="text-5xl font-extrabold mb-12 text-gray-800 drop-shadow-md text-center">
        Despre Noi
      </h2>

      <div className="bg-white/90 p-12 rounded-3xl shadow-2xl max-w-4xl border border-transparent bg-gradient-to-r from-indigo-400 via-purple-400 to-pink-400 bg-clip-padding backdrop-blur-md space-y-16 relative z-10">
        <Section
          icon={<Rocket className="text-indigo-600 w-8 h-8 transition-transform duration-300 hover:scale-110" />}
          title="Cum a început totul"
          paragraphs={[
            "Proiectul nostru a pornit din dorința unei echipe mici de a schimba modul în care oamenii trăiesc turismul.",
            "În 2020, am decis să transformăm ideea într-o realitate, oferind nu doar servicii, ci aventuri memorabile.",
          ]}
        />
        <Section
          icon={<Star className="text-yellow-500 w-8 h-8 transition-transform duration-300 hover:scale-110" />}
          title="Cum ne facem remarcați"
          paragraphs={[
            "Ne-am construit reputația prin muncă, inovație și grijă constantă față de client.",
            "Ne diferențiem prin profesionalism, prețuri echilibrate și flexibilitate adaptată fiecărui tip de călător.",
          ]}
        />
        <Section
          icon={<Users className="text-green-600 w-8 h-8 transition-transform duration-300 hover:scale-110" />}
          title="Echipa Noastră"
          paragraphs={[
            "Suntem o echipă diversă, pasionată de turism și tehnologie.",
            "Fiecare membru contribuie cu expertiză unică – de la design UI, la relații cu clienții – pentru a crea o experiență completă.",
          ]}
        />
        <Section
          icon={<Info className="text-blue-500 w-8 h-8 transition-transform duration-300 hover:scale-110" />}
          title="De ce să alegi serviciile noastre?"
          paragraphs={[
            "Pentru că oferim mai mult decât o vacanță – oferim povești.",
            "Personalizăm fiecare experiență, respectăm bugetul tău și suntem alături de tine în fiecare etapă a călătoriei.",
          ]}
        />
      </div>

      <button
        onClick={() => navigate("/")}
        className="mt-14 bg-indigo-600 text-white text-lg font-semibold py-3 px-8 rounded-full hover:bg-indigo-700 shadow-md hover:shadow-xl transition-all duration-300 flex items-center gap-2"
      >
        <span className="transform rotate-180 text-xl">➔</span> Înapoi la Home
      </button>
    </div>
  );
};

const Section = ({
  icon,
  title,
  paragraphs,
}: {
  icon?: React.ReactNode;
  title: string;
  paragraphs: string[];
}) => (
  <section className="space-y-6 max-w-prose mx-auto p-6 rounded-xl hover:shadow-lg hover:scale-[1.02] transition-transform duration-300 bg-white/80 backdrop-blur-sm">
    <div className="flex items-center gap-4">
      {icon}
      <h3 className="text-3xl font-extrabold text-gray-900 border-b-4 border-indigo-600 pb-1">{title}</h3>
    </div>
    {paragraphs.map((text, index) => (
      <p key={index} className="text-gray-800 leading-loose text-lg">
        {text}
      </p>
    ))}
  </section>
);

export default AboutPage;
