import React, { useState } from 'react';

const symbols = ['üçí', 'üçã', 'üîî', 'üçâ', '‚≠ê', '7Ô∏è‚É£'];

const getRandomSymbol = () => {
  const index = Math.floor(Math.random() * symbols.length);
  return symbols[index];
};

const SlotMachine: React.FC = () => {
  const [reels, setReels] = useState<string[]>(['üçí', 'üçí', 'üçí']);
  const [result, setResult] = useState<string>('');

  const spin = () => {
    const newReels = [getRandomSymbol(), getRandomSymbol(), getRandomSymbol()];
    setReels(newReels);

    if (newReels[0] === newReels[1] && newReels[1] === newReels[2]) {
      setResult('üéâ Ai c√¢»ôtigat! üéâ');
    } else {
      setResult('√éncearcƒÉ din nou!');
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h2>Slot Machine</h2>
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '1.5rem',
          fontSize: '4rem',
          marginBottom: '1rem',
        }}
      >
        {reels.map((symbol, i) => (
          <div
            key={i}
            style={{
              width: '80px',
              height: '80px',
              lineHeight: '80px',
              border: '3px solid #7c3aed',
              borderRadius: '12px',
              userSelect: 'none',
            }}
          >
            {symbol}
          </div>
        ))}
      </div>
      <button
        onClick={spin}
        style={{
          padding: '0.75rem 2rem',
          fontSize: '1.25rem',
          backgroundColor: '#7c3aed',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
        }}
      >
        Spin
      </button>
      <p style={{ marginTop: '1rem', fontSize: '1.25rem', fontWeight: '600' }}>
        {result}
      </p>
    </div>
  );
};

export default SlotMachine;
