import {
  PaperAirplaneIcon,
  UserIcon,
  EnvelopeIcon,
  PencilIcon,
  ChatBubbleLeftRightIcon,
} from "@heroicons/react/24/solid";
import { useState } from "react";

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });
  const [isSending, setIsSending] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSending(true);
    setSuccessMessage("");

    // Simulează trimiterea mesajului
    setTimeout(() => {
      console.log(formData);
      setIsSending(false);
      setSuccessMessage("Mesaj trimis cu succes!");
      setFormData({ name: "", email: "", subject: "", message: "" });

      // Șterge mesajul după 3 secunde
      setTimeout(() => setSuccessMessage(""), 3000);
    }, 1500);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 px-6 py-12 animate-fadeIn">
      <h2 className="text-4xl font-extrabold text-indigo-700 mb-10 relative after:absolute after:bottom-0 after:left-0 after:w-24 after:h-1 after:bg-indigo-400 after:rounded-sm after:mt-2">
        Contactează-ne
      </h2>

      {successMessage && (
        <div className="mb-6 text-green-600 font-medium bg-green-100 border border-green-300 px-4 py-2 rounded-lg shadow">
          {successMessage}
        </div>
      )}

      <form
        onSubmit={handleSubmit}
        className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-lg space-y-6"
        noValidate
      >
        <div className="grid grid-cols-1 gap-5 md:grid-cols-2">
          <div className="relative">
            <UserIcon className="w-5 h-5 text-indigo-400 absolute left-4 top-1/2 -translate-y-1/2 pointer-events-none" />
            <input
              type="text"
              name="name"
              placeholder="Nume"
              value={formData.name}
              onChange={handleChange}
              className="w-full pl-12 p-4 border border-gray-300 rounded-lg placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-indigo-400 focus:border-indigo-500 transition"
              required
            />
          </div>
          <div className="relative">
            <EnvelopeIcon className="w-5 h-5 text-indigo-400 absolute left-4 top-1/2 -translate-y-1/2 pointer-events-none" />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              className="w-full pl-12 p-4 border border-gray-300 rounded-lg placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-indigo-400 focus:border-indigo-500 transition"
              required
            />
          </div>
          <div className="relative md:col-span-2">
            <PencilIcon className="w-5 h-5 text-indigo-400 absolute left-4 top-1/2 -translate-y-1/2 pointer-events-none" />
            <input
              type="text"
              name="subject"
              placeholder="Subiect"
              value={formData.subject}
              onChange={handleChange}
              className="w-full pl-12 p-4 border border-gray-300 rounded-lg placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-indigo-400 focus:border-indigo-500 transition"
              required
            />
          </div>
        </div>
        <div className="relative">
          <ChatBubbleLeftRightIcon className="w-5 h-5 text-indigo-400 absolute left-4 top-4 pointer-events-none" />
          <textarea
            name="message"
            placeholder="Mesaj"
            value={formData.message}
            onChange={handleChange}
            className="w-full pl-12 p-4 border border-gray-300 rounded-lg placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-indigo-400 focus:border-indigo-500 transition resize-none h-32"
            required
          />
        </div>

        {/* Buton de trimitere */}
        <div className="flex justify-center">
          <button
            type="submit"
            disabled={isSending}
            className={`flex justify-center items-center gap-2 bg-gradient-to-r from-indigo-500 to-indigo-700 text-white py-3 px-8 rounded-lg shadow-md transition duration-300 ${
              isSending ? "opacity-50 cursor-not-allowed" : "hover:scale-105 hover:shadow-lg"
            }`}
          >
            {isSending ? (
              <span className="flex items-center gap-2">
                <svg
                  className="animate-spin h-5 w-5 text-white"
                  viewBox="0 0 24 24"
                  fill="none"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
                  ></path>
                </svg>
                Se trimite...
              </span>
            ) : (
              <>
                Trimite Mesajul
                <PaperAirplaneIcon className="w-5 h-5" />
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ContactPage;
