import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

interface Client {
  idClient: number;
  emailClient: string;
  numeClient: string;
  prenumeClient: string;
}

interface Rezervare {
  id: number;
  oraRezervare: string;
  orasPlecare: string;
  orasSosire: string;
}

const ProfilePage = () => {
  const [client, setClient] = useState<Client | null>(null);
  const [rezervari, setRezervari] = useState<Rezervare[]>([]);
  const navigate = useNavigate();

  // Form fields pentru editare
  const [editNume, setEditNume] = useState("");
  const [editPrenume, setEditPrenume] = useState("");
  const [editEmail, setEditEmail] = useState("");

  useEffect(() => {
    const clientId = localStorage.getItem("clientId");
    if (!clientId) {
      return;
    }

    axios.get(`https://localhost:7239/api/Client/${clientId}`)
      .then(response => {
        setClient(response.data);
        setEditNume(response.data.numeClient);
        setEditPrenume(response.data.prenumeClient);
        setEditEmail(response.data.emailClient);
      })
      .catch(error => {
        console.error("Eroare la preluarea datelor client:", error);
      });

    axios.get(`https://localhost:7239/api/Rezervari/client/${clientId}`)
      .then(response => {
        setRezervari(response.data);
      })
      .catch(error => {
        console.error("Eroare la preluarea rezervărilor:", error);
      });
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("clientId");
    navigate("/login");
  };

  const handleDeleteAccount = () => {
    if (!client) return;

    axios.delete(`https://localhost:7239/api/Client/${client.idClient}`)
      .then(() => {
        localStorage.removeItem("clientId");
        navigate("/register");
      })
      .catch(error => {
        console.error("Eroare la ștergerea contului:", error);
      });
  };

  const handleUpdateProfile = () => {
    if (!client) return;

    const updatedClient = {
      ...client,
      numeClient: editNume,
      prenumeClient: editPrenume,
      emailClient: editEmail
    };

    axios.put(`https://localhost:7239/api/Client/${client.idClient}`, updatedClient)
      .then(() => {
        setClient(updatedClient);
        alert("Datele au fost actualizate.");
      })
      .catch(error => {
        console.error("Eroare la actualizare profil:", error);
      });
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) {
      return "Data invalidă";
    }
    return date.toLocaleDateString() + " " + date.toLocaleTimeString();
  };

  return (
    <div className="max-w-lg mx-auto p-8 bg-gradient-to-br from-indigo-50 to-white rounded-2xl shadow-lg mt-12">
      <h2 className="text-3xl font-bold text-indigo-800 mb-6 text-center">Profilul Meu</h2>

      {client ? (
        <>
          <div className="mb-8 space-y-4 text-gray-700">
            <div>
              <label className="font-semibold">Nume:</label>
              <input
                type="text"
                value={editNume}
                onChange={(e) => setEditNume(e.target.value)}
                className="block w-full border rounded p-2 mt-1"
              />
            </div>
            <div>
              <label className="font-semibold">Prenume:</label>
              <input
                type="text"
                value={editPrenume}
                onChange={(e) => setEditPrenume(e.target.value)}
                className="block w-full border rounded p-2 mt-1"
              />
            </div>
            <div>
              <label className="font-semibold">Email:</label>
              <input
                type="email"
                value={editEmail}
                onChange={(e) => setEditEmail(e.target.value)}
                className="block w-full border rounded p-2 mt-1"
              />
            </div>

            <button
              onClick={handleUpdateProfile}
              className="w-full py-3 bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg font-semibold transition-colors mt-4"
            >
              Salvează Modificările
            </button>
          </div>

          <h3 className="text-xl font-semibold text-indigo-700 mb-4">Rezervările mele</h3>
          {rezervari.length > 0 ? (
            <ul className="space-y-4 max-h-72 overflow-y-auto">
              {rezervari.map((rezervare) => (
                <li key={rezervare.id} className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow">
                  <p className="font-semibold text-indigo-600">
                    {rezervare.orasPlecare} &rarr; {rezervare.orasSosire}
                  </p>
                  <p className="text-gray-500 text-sm mt-1">
                    Data rezervării: {formatDate(rezervare.oraRezervare)}
                  </p>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500 italic">Nu ai rezervări făcute.</p>
          )}

          <button
            onClick={handleLogout}
            className="mt-8 w-full py-3 bg-red-600 hover:bg-red-700 text-white rounded-lg font-semibold transition-colors"
          >
            Logout
          </button>

          <button
            onClick={handleDeleteAccount}
            className="mt-4 w-full py-3 bg-gray-500 hover:bg-gray-600 text-white rounded-lg font-semibold transition-colors"
          >
            Șterge Contul
          </button>
        </>
      ) : (
        <p className="text-center text-gray-600 italic">Nu ești autentificat. Te rugăm să te loghezi.</p>
      )}
    </div>
  );
};

export default ProfilePage;
