// src/pages/HomePage.tsx
import React from "react";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";

const offers = [
  {
    title: "Vacanță în Grecia",
    price: "De la 199€ pe persoană",
    image: "/images/grecia.jpg",
    bgColor: "bg-pink-600",
  },
  {
    title: "Vacanță în Italia",
    price: "De la 250€ pe persoană",
    image: "/images/italia.jpg",
    bgColor: "bg-green-600",
  },
  {
    title: "Vacanță în Spania",
    price: "De la 199€ pe persoană",
    image: "/images/spania.jpg",
    bgColor: "bg-blue-600",
  },
  {
    title: "Vacanță în Franța",
    price: "De la 280€ pe persoană",
    image: "/images/franta.jpg",
    bgColor: "bg-yellow-600",
  },
  {
    title: "Vacanță în Portugalia",
    price: "De la 230€ pe persoană",
    image: "/images/portugalia.jpg",
    bgColor: "bg-red-600",
  },
];

const HomePage = () => {
  return (
    <div className="min-h-screen bg-white">
      <main className="p-8">
        <h2 className="text-4xl font-bold mb-12 text-center">
          Oferte de sezon
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
          {offers.map((offer, index) => (
            <div
              key={index}
              className={`p-6 rounded-2xl shadow-lg flex flex-col items-center ${offer.bgColor}`}
            >
              <h3 className="text-2xl font-semibold mb-3 text-white">
                {offer.title}
              </h3>
              <p className="text-lg mb-4 text-white">{offer.price}</p>
              <div className="w-full h-[200px] rounded-lg overflow-hidden mb-4">
                <img
                  src={offer.image}
                  alt={offer.title}
                  className="w-full h-full object-cover"
                />
              </div>
              <Link
                to="/flights"
                className="bg-white text-black font-semibold px-6 py-2 rounded-full hover:bg-opacity-90 transition"
              >
                Rezervă acum!
              </Link>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
