import React, { useState, ChangeEvent } from 'react';

interface User {
  id: number;
  username: string;
  email: string;
  sold: number;
  avatarUrl: string; // nume fiÈ™ier din /images/avatars/
}

const predefinedAvatars = [
  { id: 'user1', filename: 'user1.png' },
  { id: 'user2', filename: 'user2.png' },
  { id: 'user3', filename: 'user3.png' },
  { id: 'user4', filename: 'user4.png' },
];

const dummyUser: User = {
  id: 1,
  username: 'alex23',
  email: 'alex23@example.com',
  sold: 250.75,
  avatarUrl: predefinedAvatars[0].filename, // "user1.png"
};

const Profile: React.FC = () => {
  const [user, setUser] = useState<User>(dummyUser);
  const [uploadError, setUploadError] = useState<string | null>(null);

  const handleLogout = () => {
    alert('Logout realizat.');
    // logicÄƒ logout/redirect aici
  };

  const handleAvatarSelect = (filename: string) => {
    setUser(prev => ({ ...prev, avatarUrl: filename }));
    setUploadError(null);
  };

  const handleFileUpload = async (e: ChangeEvent<HTMLInputElement>) => {
    setUploadError(null);
    const file = e.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      setUploadError('Te rugÄƒm sÄƒ Ã®ncarci un fiÈ™ier imagine valid.');
      return;
    }
    if (file.size > 2 * 1024 * 1024) {
      setUploadError('FiÈ™ierul este prea mare. Maxim 2MB.');
      return;
    }

    // Upload cÄƒtre backend
    const formData = new FormData();
    formData.append('avatar', file);

    try {
      const res = await fetch('/api/upload-avatar', {
        method: 'POST',
        body: formData,
      });
      const data = await res.json();
      if (data.success && data.filename) {
        setUser(prev => ({ ...prev, avatarUrl: data.filename }));
      } else {
        setUploadError('Eroare la Ã®ncÄƒrcarea imaginii.');
      }
    } catch (err) {
      setUploadError('Eroare la upload. ÃŽncearcÄƒ din nou.');
      console.error(err);
    }
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: "url('/images/background-casino.jpg')",
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
        paddingTop: '4rem',
        paddingBottom: '4rem',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: '#374151',
      }}
    >
      <div
        style={{
          maxWidth: '480px',
          width: '100%',
          backgroundColor: 'rgba(255, 255, 255, 0.35)',
          padding: '2.5rem 2rem',
          borderRadius: '20px',
          boxShadow: '0 20px 40px rgba(0,0,0,0.12)',
          backdropFilter: 'blur(10px)',
          WebkitBackdropFilter: 'blur(10px)',
          userSelect: 'none',
          textAlign: 'center',
          transition: 'box-shadow 0.3s ease',
        }}
        onMouseEnter={e => {
          (e.currentTarget as HTMLDivElement).style.boxShadow = '0 25px 50px rgba(0,0,0,0.2)';
        }}
        onMouseLeave={e => {
          (e.currentTarget as HTMLDivElement).style.boxShadow = '0 20px 40px rgba(0,0,0,0.12)';
        }}
      >
        {/* Avatar */}
        <img
          src={`/images/avatars/${user.avatarUrl}`}
          alt={`${user.username} avatar`}
          style={{
            width: 120,
            height: 120,
            borderRadius: '50%',
            marginBottom: '1.5rem',
            boxShadow: '0 5px 15px rgba(0,0,0,0.15)',
            objectFit: 'cover',
            userSelect: 'none',
          }}
          draggable={false}
        />

        {/* Avatar Selection */}
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '1rem',
            marginBottom: '1.5rem',
            flexWrap: 'wrap',
          }}
        >
          {predefinedAvatars.map(avatar => (
            <button
              key={avatar.id}
              onClick={() => handleAvatarSelect(avatar.filename)}
              aria-label={`Select avatar ${avatar.id}`}
              style={{
                border:
                  user.avatarUrl === avatar.filename ? '3px solid #059669' : '2px solid transparent',
                borderRadius: '50%',
                padding: 0,
                cursor: 'pointer',
                transition: 'border-color 0.3s',
                width: 60,
                height: 60,
                backgroundImage: `url(/images/avatars/${avatar.filename})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                boxShadow:
                  user.avatarUrl === avatar.filename ? '0 0 8px #10b981' : '0 2px 6px rgba(0,0,0,0.1)',
              }}
            />
          ))}
        </div>

        {/* Upload custom avatar */}
        <label
          htmlFor="avatar-upload"
          style={{
            display: 'inline-block',
            backgroundColor: '#059669',
            color: 'white',
            padding: '0.6rem 1.2rem',
            borderRadius: '10px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '1rem',
            userSelect: 'none',
            marginBottom: '1rem',
          }}
        >
          ÃŽncarcÄƒ o pozÄƒ
          <input
            id="avatar-upload"
            type="file"
            accept="image/*"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
        </label>

        {uploadError && (
          <p
            style={{
              color: '#b91c1c',
              fontWeight: '600',
              marginBottom: '1rem',
              userSelect: 'text',
            }}
          >
            {uploadError}
          </p>
        )}

        {/* Title */}
        <h1
          style={{
            fontSize: '2.25rem',
            fontWeight: '700',
            marginBottom: '2rem',
            color: '#059669',
            userSelect: 'text',
          }}
        >
          ðŸ‘¤ Profil Utilizator
        </h1>

        {/* Info */}
        <div style={{ textAlign: 'left', userSelect: 'text' }}>
          <ProfileField label="Username" value={user.username} icon="ðŸ‘¤" />
          <ProfileField label="Email" value={user.email} icon="âœ‰ï¸" />
          <ProfileField
            label="Sold cont"
            value={`${user.sold.toLocaleString('ro-RO', {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })} RON`}
            icon="ðŸ’°"
            highlight
          />
        </div>

        {/* Logout Button */}
        <button
          onClick={handleLogout}
          style={{
            marginTop: '2.5rem',
            width: '100%',
            backgroundColor: '#ef4444',
            color: 'white',
            padding: '0.9rem',
            border: 'none',
            borderRadius: '14px',
            cursor: 'pointer',
            fontWeight: '700',
            fontSize: '1.15rem',
            transition: 'background-color 0.3s ease, transform 0.1s ease',
            userSelect: 'none',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
          }}
          onMouseEnter={e => (e.currentTarget.style.backgroundColor = '#b91c1c')}
          onMouseLeave={e => (e.currentTarget.style.backgroundColor = '#ef4444')}
          onMouseDown={e => (e.currentTarget.style.transform = 'scale(0.95)')}
          onMouseUp={e => (e.currentTarget.style.transform = 'scale(1)')}
        >
          Deconectare
        </button>
      </div>
    </div>
  );
};

interface ProfileFieldProps {
  label: string;
  value: string;
  icon: string;
  highlight?: boolean;
}

const ProfileField: React.FC<ProfileFieldProps> = ({ label, value, icon, highlight }) => (
  <p
    style={{
      fontWeight: highlight ? '700' : '500',
      marginBottom: '1rem',
      fontSize: '1.1rem',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      color: highlight ? '#059669' : '#374151',
    }}
  >
    <span style={{ fontSize: '1.3rem' }}>{icon}</span>
    <strong style={{ minWidth: '90px', display: 'inline-block' }}>{label}:</strong> {value}
  </p>
);

export default Profile;
